INSERT INTO `quest_template_locale` VALUES (47122, 'ptBR', 'Tumba de Sargeras: O Avatar Caído', 'Derrote o Avatar Caído na Tumba de Sargeras.', 'Só falta o Avatar Caído e depois a batalha final. Mate-o e vamos acabar logo com isso.', '', '', '', '', '', '', 0);
