INSERT INTO `quest_template_locale` VALUES (47126, 'deDE', 'Das Grabmal des Sargeras: Der Betrüger', 'Besiegt Kil\'jaeden im Grabmal des Sargeras.', 'Nur die mächtigsten seiner Diener dürfen dieses Siegel tragen. Es offenbart die Anwesenheit des Betrügers, Kil\'jaeden. Wir müssen ihn besiegen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47126, 'esES', 'Tumba de Sargeras: El Impostor', 'Derrota a Kil\'jaeden en la Tumba de Sargeras.', 'Tan solo portado por sus más poderosos sirvientes, el sello indica la presencia del Impostor, Kil\'jaeden. Debe ser derrotado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47126, 'esMX', 'Tumba de Sargeras: El Falsario', 'Derrota a Kil\'jaeden en la Tumba de Sargeras.', 'Solo los sirvientes más poderosos de Kil\'jaeden llevan estos sellos. El Falsario debe estar aquí y hay que derrotarlo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47126, 'frFR', 'Tombe de Sargeras : le Trompeur', 'Terrassez Kil’jaeden dans la Tombe de Sargeras.', 'Réservé à ses serviteurs les plus puissants, le sceau indique la présence de Kil’jaeden le Trompeur. Il faut à tout prix l’éliminer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47126, 'itIT', 'Tomba di Sargeras: L\'Ingannatore', 'Sconfiggi Kil\'jaeden nella Tomba di Sargeras.', 'Il sigillo, utilizzato solamente dai più potenti servitori dell\'Ingannatore, Kil\'jaeden, è segno della sua presenza in questo posto. Deve essere sconfitto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47126, 'koKR', '살게라스의 무덤: 기만자', '살게라스의 무덤에서 킬제덴을 처치해야 합니다.', '기만자 킬제덴의 가장 강력한 하수인들만 지니고 있다는 인장이 눈에 띄는 걸 보니, 킬제덴이 마침내 나타난 것 같습니다. 그를 반드시 처치해야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47126, 'ptBR', 'Tumba de Sargeras: O Enganador', 'Derrote Kil\'jaeden na Tumba de Sargeras.', 'Portado exclusivamente pelos servos mais poderosos, o Selo indica a presença de Kil\'jaeden, o Enganador. Temos que derrotá-lo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47126, 'ruRU', 'Гробница Саргераса: Искуситель', 'Победите Кил\'джедена в гробнице Саргераса.', 'Печатью отмечены только самые могущественные прислужники Кил\'джедена, а это значит, что Искуситель где-то рядом. Его нужно уничтожить.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47126, 'zhCN', '萨格拉斯之墓：欺诈者', '击败萨格拉斯之墓的基尔加丹。', '这种印记只有欺诈者基尔加丹最强大的仆从才会携带，它意味着基尔加丹的出现。一定要击败他。', '', '', '', '', '', '', 0);
