INSERT INTO `quest_template_locale` VALUES (47129, 'itIT', 'Tomba di Sargeras: L\'Ingannatore', 'Sconfiggi Kil\'jaeden nella Tomba di Sargeras.', 'Il sigillo, utilizzato solamente dai più potenti servitori dell\'Ingannatore, Kil\'jaeden, è segno della sua presenza in questo posto. Deve essere sconfitto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47129, 'koKR', '살게라스의 무덤: 기만자', '살게라스의 무덤에서 킬제덴을 처치해야 합니다.', '기만자 킬제덴의 가장 강력한 하수인들만 지니고 있다는 인장이 눈에 띄는 걸 보니, 킬제덴이 마침내 나타난 것 같습니다. 그를 반드시 처치해야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47129, 'ptBR', 'Tumba de Sargeras: O Enganador', 'Derrote Kil\'jaeden na Tumba de Sargeras.', 'Portado exclusivamente pelos servos mais poderosos, o Selo indica a presença de Kil\'jaeden, o Enganador. Temos que derrotá-lo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47129, 'ruRU', 'Гробница Саргераса: Искуситель', 'Победите Кил\'джедена в гробнице Саргераса.', 'Печатью отмечены только самые могущественные прислужники Кил\'джедена, а это значит, что Искуситель где-то рядом. Его нужно уничтожить.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47129, 'zhCN', '萨格拉斯之墓：欺诈者', '击败萨格拉斯之墓的基尔加丹。', '这种印记只有欺诈者基尔加丹最强大的仆从才会携带，它意味着基尔加丹的出现。一定要击败他。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47129, 'zhTW', '薩格拉斯之墓：欺詐者', '在薩格拉斯之墓中擊敗基爾加丹。', '只有欺詐者基爾加丹最強大的僕人才會佩戴這種徽印。這表示基爾加丹已經來到薩格拉斯之墓了。我們必須打倒基爾加丹。', '', '', '', '', '', '', 0);
