INSERT INTO `quest_template_locale` VALUES (47589, 'deDE', 'Zorn der Gerechtigkeit', 'Schaltet $1oa Verwüster der Legion aus.', 'Das Bombardement durch die Verwüster hält den Vorstoß unserer Truppen auf. Ich kann uns nur für begrenzte Zeit vor diesem Beschuss schützen.$B$BChampion, Ihr müsst durch ihre Linien brechen und die Barrieren zerstören, welche die Verwüster beschützen.$B$BWenn es Euch gelingt, die Anker, mit denen die Schilde aufrechterhalten werden, zu vernichten, kann ich sie aus der Ferne ausschalten.', '', '', '', '', '', 'Trefft Prophet Velen an der Krone der Zerstörung.', 0);
INSERT INTO `quest_template_locale` VALUES (47589, 'esES', 'Furia recta', 'Destruye a $1oa devastadores de la Legión.', 'El bombardeo de los devastadores nos impide avanzar. No podré protegernos de sus explosiones durante mucho tiempo.$b$b$gCampeón:Campeona;, atraviesa sus filas para eliminar las barreras que protegen a los devastadores.$b$bSi logras destruir los anclajes que sujetan los escudos, yo podré purgarlos desde lejos.', '', '', '', '', '', 'Reúnete con: Profeta Velen. Zona: Corona de Destrucción.', 0);
INSERT INTO `quest_template_locale` VALUES (47589, 'esMX', 'Furia recta', 'Destruye $1oa devastadores de la Legión.', 'Los devastadores bombardean nuestras fuerzas y nos impiden seguir avanzando. No puedo seguir protegiéndonos de sus explosiones por siempre.$B$B$gCampeón:Campeona;, tienes que atravesar sus líneas y eliminar las barreras que protegen a los devastadores.$B$BSi logras destruir las anclas que potencian los escudos, yo los eliminaré desde lejos.', '', '', '', '', '', 'Reúnete con el Profeta Velen en la Corona de Destrucción.', 0);
INSERT INTO `quest_template_locale` VALUES (47589, 'frFR', 'Fureur vertueuse', 'Détruisez $1oa Dévastateurs de la Légion.', 'Les dévastateurs qui bombardent nos armées nous empêchent de progresser, sans compter que je ne pourrai pas nous protéger indéfiniment de leurs attaques.$B$B$gChampion:Championne;, vous devez franchir leurs lignes et abattre les barrières qui protègent les dévastateurs.$B$BSi vous parvenez à détruire les ancrages qui maintiennent leurs boucliers en place, je pourrai les purifier à distance.', '', '', '', '', '', 'Allez voir le Prophète Velen à la Couronne de la destruction.', 0);
INSERT INTO `quest_template_locale` VALUES (47589, 'itIT', 'La giusta furia', 'Distruggi $1oa Devastatori della Legione.', 'I Devastatori che stanno bombardando le nostre forze ci impediscono di avanzare ulteriormente. Non potrò proteggerci dai loro attacchi per sempre.$B$BDevi assolutamente sfondare le loro linee e rimuovere le barriere che proteggono i loro Devastatori.$B$BSe riuscirai a distruggere gli ancoraggi che mantengono in piedi gli scudi, potrò colpirli da lontano.', '', '', '', '', '', 'Incontra il Profeta Velen alla Corona della Distruzione.', 0);
INSERT INTO `quest_template_locale` VALUES (47589, 'koKR', '정의로운 분노', '군단 파멸포 $1oa개를 파괴해야 합니다.', '우리 병력에 공격을 퍼붓는 파멸포 때문에 더 앞으로 나아가지 못하고 있네. 내가 포격을 막는 데도 한계가 있네.$B$B영웅이여, 저들의 전선을 뚫고 들어가 파멸포를 보호하는 방어막을 제거해 주게.$B$B자네가 방어막을 유지하는 수정을 파괴하면, 내가 멀리서 파멸포를 쓸어낼 수 있네.', '', '', '', '', '', '파괴의 마루에 있는 예언자 벨렌을 만나야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (47589, 'ptBR', 'Fúria íntegra', 'Destrua $1oa Devastadores da Legião.', 'Não temos como avançar, pois os devastadores bombardeiam nossas tropas. Não sei por quanto tempo conseguirei nos proteger desses ataques.$B$B$gCampeão:Campeã;, você precisa penetrar na linha inimiga e acabar com as barreiras que protegem os devastadores.$B$BDestrua as âncoras que energizam os escudos para que eu dê um jeito neles de longe.', '', '', '', '', '', 'Fale com o Profeta Velen na Coroa da Destruição.', 0);
INSERT INTO `quest_template_locale` VALUES (47589, 'ruRU', 'Праведное неистовство', 'Разрушьте $1oa |4уничтожитель:уничтожителя:уничтожителей; Легиона.', 'Уничтожители обстреливают наши войска, не позволяя продвигаться дальше. Долго защищать нас от их снарядов я не смогу.$B$BТебе нужно пробиться за линию обороны врага и ликвидировать барьеры, окружающие уничтожители.$B$BЕсли ты сможешь разрушить якоря, поддерживающие щиты, я уничтожу их.', '', '', '', '', '', 'Встретьтесь с пророком Веленом у вершины Разрушения.', 0);
INSERT INTO `quest_template_locale` VALUES (47589, 'zhCN', '正义之怒', '摧毁$1oa个军团灭绝者。', '灭绝者正在轰击我们，阻止我们前进。我只能保护我们不受爆炸的伤害。$B$B勇士，你必须突破他们的防线，消除那个保护灭绝者的屏障。$B$B如果你能摧毁维持护盾的锚，我就可以从远处净化他们。', '', '', '', '', '', '与毁灭之冠的先知维伦见面。', 0);
INSERT INTO `quest_template_locale` VALUES (47589, 'zhTW', '正義之怒', '摧毀$1oa個軍團毀滅者。', '毀滅者正在轟炸我軍部隊，讓我們無法前進。我只能再擋一陣子。$B$B勇士，你必須突破它們的陣線，關閉保護毀滅者的屏障。$B$B如果你能摧毀維持屏障的定位器，我就能從遠處淨化它們。', '', '', '', '', '', '在毀滅之冠與預言者費倫碰面。', 0);
INSERT INTO `quest_template_locale` VALUES (47591, 'deDE', 'Wollt Ihr \'ne Lampe kaufen?', 'Kauft Gaunah die dubiose Lampe ab.', 'He da, wollt Ihr \'ne Lampe kaufen? Werdet\'s nicht bereuen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47591, 'esES', '¿Quieres comprar un candil?', 'Cómprale el candil sospechoso a Griftah.', '¿Oye, me comprah ehte candil? No te arrepentiráh.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47591, 'esMX', '¿Quieres comprar una lámpara?', 'Cómprale la lámpara dudosa a Griftah.', 'Oye, ¿quieres comprar una lámpara? No te arrepentirás.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47591, 'frFR', 'Une lampe, ça vous tente ?', 'Achetez la Lampe suspecte auprès d’Eskrokar.', 'Hé mec, une lampe, ça vous tente ? C’est l’affaire du siècle.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47591, 'itIT', 'Vuoi comprare una lampada?', 'Compra la Lampada Sospetta da Griftah.', 'Cumpà, vuoi comprare na lampada? Nun te ne pentirai.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47591, 'koKR', '등불 하나 살 텐가?', '그립타에게서 미심쩍은 등불을 구입해야 합니다.', '어이, 등불 하나 살 텐가? 후회 안 할 걸세.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47591, 'ptBR', 'Quer comprar uma lâmpada?', 'Comprar a Lâmpada Suspeita de Trambik.', 'Opa, quer comprar uma lâmpada? Tu não vai se arrepender.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47591, 'ruRU', 'Хочешь купить лампу?', 'Купите подозрительную лампу у Жуля.', 'Эй, хочешь купить лампу? Не пожалеешь! Гарантирую!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47591, 'zhCN', '想买灯吗？', '从格里伏塔处购买可疑的灯具。', '嘿，想买灯吗？你不会后悔的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47591, 'zhTW', '買燈嗎？', '跟格利夫塔購買可疑的油燈。', '喂，要買燈嗎？你不會後悔的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47592, 'deDE', 'Glänzend wie ein Diamant', 'Kauft ein zertifiziert biologisches Lampenputzset von Gaunah und säubert damit die schmierige Lampe.', 'Oh, tut mir leid. Die Lampe könnt \'n bisschen Pflege gebrauchen. Hab ich was für da.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47592, 'esES', 'Flamante como un diamante', 'Cómprale un juego de pulimentar candiles orgánico certificado a Griftah y úsalo para sacar brillo al candil mugriento.', 'Vaya, lo siento. Parece qu\'ese candil necesita un buen pulido. Y tengo juhto lo necesario.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47592, 'esMX', 'Cerdos y diamantes', 'Cómprale un kit limpiador orgánico certificado para lámparas a Griftah y úsalo para pulir la lámpara mugrienta.', 'Ay, lo siento. A esa lámpara le vendría bien un poco de brillo. Tengo justo lo que necesita.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47592, 'frFR', 'Qu’elle brille de mille feux', 'Achetez une Trousse de nettoyant pour lampe certifié bio auprès d’Eskrokar et utilisez-la pour astiquer la Lampe crasseuse.', 'Oh, ’scusez. C’te lampe, c’est vrai qu’elle pourrait briller un peu plus. Par chance, j’ai justement c’qui faut.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47592, 'itIT', 'Brillante come un diamante', 'Compra un Kit Certificato per Lucidatura Lampade da Griftah e usalo per lucidare la Lampada Sporca.', 'Oh, che peccato. Pare che chella lampada tenga bisogno di na bella lucidata. Tengo giusto chello che fa per noi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47592, 'koKR', '다이아몬드처럼 밝게 빛나라', '그립타에게서 친환경 인증 획득 등불 광내기 키트를 구입하여 더러워진 등불을 광내는 데 사용해야 합니다.', '오, 미안하네. 등불에 광을 좀 낼 수 있을 것 같네. 마침 딱 좋은 게 있네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47592, 'ptBR', 'Brilhoso feito um diamante', 'Comprar um Kit de Polimento Orgânico Certificado para Lâmpadas de Trambik e usar para polir a Lâmpada Encardida.', 'Ih, foi mal aí. Parece que essa lâmpada precisa dum brilho. E eu tenho a parada certa pra mandar ver.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47592, 'ruRU', 'Сияй подобно бриллианту', 'Купите сертифицированный экологически чистый набор для очистки ламп у Жуля и отполируйте грязную лампу.', 'Ой, извини. Это лампу неплохо бы отчистить. У меня есть подходящее средство.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47592, 'zhCN', '钻石般闪亮', '从格里伏塔处购买一个权威认证有机灯具抛光工具包，用它给肮脏的灯具抛光。', '哦，抱歉。那个灯好像需要擦擦。我这正好有工具。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47592, 'zhTW', '像鑽石一樣閃閃發光', '跟格利夫塔購買純天然有機燈具清潔工具組，然後清潔滿是油垢的燈。', '喔，抱歉，那盞燈看起來需要好好清洗才能用。我剛好有在賣清潔工具。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47594, 'deDE', 'Die nehm\' ich, danke!', 'Besorgt 10.000 Münzen der Luft für Gaunah.', 'Die Münz\'n hier lösen sich \"puff\" in Luft auf? Das passt ja, wie die Faust auf\'s Auge. Ich weiß genau, wem ich die stecke.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47594, 'esES', '¡Me las quedo, gracias!', 'Reúne 10 000 monedas de aire para Griftah.', '¿Diceh qu\'ehtah monedah s\'ehfuman en el aire? Ehtupendo, porque ya sé a quién dárselah.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47594, 'esMX', '¡Yo me las quedo, gracias!', 'Consigue 10,000 Monedas de aire para Griftah.', '¿Dices que estas monedas se desvanecen en el aire? Arr, esto parece perfecto. Conozco al sujeto ideal para entregárselas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47594, 'frFR', 'J’vais vous débarrasser, merci bien !', 'Obtenez 10 000 Pièces d’air pour Eskrokar.', 'Ces pièces, vous me dites qu’elles partent en fumée ? Ça tombe bien, je connais justement un gars que ça intéresse.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47594, 'itIT', 'Le prendo io, grazie!', 'Ottieni 10.000 Monete d\'Aria per Griftah.', 'Cheste monete, hai detto che svaniscono nell\'aria? Ma è perfetto! Conosco proprio il cumpà giusto a cui darle.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47594, 'koKR', '내가 가져가겠네, 고맙네!', '그립타를 위해 바람의 주화 10,000개를 입수해야 합니다.', '공기 중으로 사라지는 주화라고? 완벽하구먼. 이걸 줄 만한 녀석이 하나 있거든.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47594, 'ptBR', 'Deixa comigo, valeu!', 'Obtenha 10.000 Moedas de Vento para Trambik.', 'Essas moedas, tu tá dizendo que elas somem no ar, é? Eu tenho o cara perfeito pra ficar com elas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47594, 'ruRU', 'Возьму без вопросов', 'Получите 10000 монет воздуха для Жуля.', 'Говоришь, эти монеты станут воздухом? Расчудесно! У меня как раз есть, кому их отдать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47594, 'zhCN', '这些我要了，谢谢！', '为格里伏塔获取10,000枚空气硬币。', '你说这些硬币会变成空气？听起来不错，我刚好知道该把它们给谁。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47594, 'zhTW', '交給我吧，謝囉！', '幫格利夫塔取得10,000枚浮雲之幣。', '你說這種硬幣，會憑空消失？聽起來太棒了，我知道要交給哪個傢伙。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47595, 'deDE', 'Brauch\' ich nicht, nehm\'s aber trotzdem', 'Sammelt 10.000 Münzen der Luft für Gaunah.', 'Wenn Ihr noch mehr sammeln wollt, stört das Gaunah kein bisschen. Aber ich brauch da jetzt nich\' mehr so viel von, also zahl\' ich Euch weniger.', 'Sammelt 9999 Münzen der Luft für Gaunah.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47595, 'esES', 'No los necesito, pero me los quedo', 'Reúne 10 000 monedas de aire para Griftah.', 'Si quiereh conseguir máh, Griftah no será quien te lo impida. Dicho ehto, yo ya no necesito máh, así que te daré menoh por elloh.', 'Reúne 9999 monedas de aire para Griftah.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47595, 'esMX', 'No las necesito, pero me las quedaré', 'Recoge 10,000 Monedas de aire para Griftah.', 'Si quieres recolectar más, Griftah no se va a oponer. De todos modos, ya no son tan urgentes, así que te pagaré un poco menos por ellas.', 'Recoge 9999 monedas de aire para Griftah.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47595, 'frFR', 'La dure loi du marché', 'Récupérez 10 000 Pièces d’air pour Eskrokar.', 'Si vous en voulez plus, c’est pas Eskrokar qui vous jettera la pierre, ça non. Mais j’vous préviens, j’en ai plus tant besoin, alors j’vous en donnerai moins.', 'Récupérer 9 999 Pièces d’air pour Eskrokar', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47595, 'itIT', 'Non mi servono, ma le accetto volentieri', 'Recupera 10.000 Monete d\'Aria per Griftah.', 'Se ne trovassi altre, Griftah nun terrà nulla in contrario. Detto chisto, nun mi servono più così tanto, quindi nun aspettarti lo stesso pagamento.', 'Recupera 9.999 Monete d\'Aria per Griftah.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47595, 'koKR', '필요는 없지만, 받아주겠네', '그립타를 위해 바람의 주화 10,000개를 모아야 합니다.', '자네가 더 모으고 싶어한다면, 나 그립타는 말리지 않겠네. 그렇긴 해도 나한테 더이상 필요 없으니, 조금 덜 쳐줄 걸세.', '그립타를 위해 바람의 주화 9999개 모으기', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47595, 'ptBR', 'Não preciso, mas vou levar mesmo assim', 'Colete 10.000 Moedas de Vento para Trambik.', 'Se você quiser ir catar mais, Trambik não vai reclamar. Só que eu não preciso de mais tanto assim, então vou te pagar menos, beleza?', 'Coletar 9999 Moedas de Vento para Trambik.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47595, 'ruRU', 'Все равно возьму', 'Соберите 10000 монет воздуха для Жуля.', 'А если захочешь собрать еще, Жуль возражать не будет. Но мне так много уже не надо, поэтому платить буду меньше.', 'Соберите 9999 монет воздуха для Жуля.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47595, 'zhCN', '我拿它们没用，但我还是要买', '为格里伏塔收集10,000枚空气硬币。', '如果你想再多收集些，格里伏塔没有意见。话虽这么说，我现在没有以前那么需要这些东西了，所以我出的价钱也会降低点。', '为格里伏塔收集9999枚空气硬币。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47595, 'zhTW', '供需過剩，但來者不拒', '幫格利夫塔取得10,000枚浮雲之幣。', '如果你想要繼續收集，格利夫塔不會有意見。不過我已經不需要那麼多了，所以你的報酬也會變少。', '為格利夫塔取得9999枚浮雲之幣。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47603, 'deDE', 'Der geheimnisvolle und famose Al\'Abas', 'Sprecht mit Al\'Abas.', 'Ich sehe Euch, $gärmlicher Sterblicher:ärmliche Sterbliche;! Ich danke Euch für die Pflege meiner Lampe!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47603, 'esES', 'El misterioso y magnífico Al\'Abas', 'Habla con Al\'Abas.', '¡Te veo, insignificante mortal! ¡Y aprecio tu preocupación por mi candil!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47603, 'esMX', 'El misterioso y magnífico Al\'Abas', 'Habla con Al\'Abas.', '¡Te veo, mortal insignificante! ¡Aprecio tu preocupación por mi lámpara!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47603, 'frFR', 'Le mystérieux Al’Abas le Magnifique', 'Parlez à Al’Abas.', 'Je vous vois, misérable $gmortel:mortelle; ! Je vous remercie d’avoir pris soin de ma lampe.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47603, 'itIT', 'Il misterioso e magnifico Al\'abas', 'Parla con Al\'abas.', 'Ti vedo, $gpiccolo:piccola; mortale! Apprezzo molto il tuo interesse per la mia lampada!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47603, 'koKR', '수수께끼의 위대한 자 알아바스', '알아바스와 대화해야 합니다.', '거기 있군, 미약한 필멸자여! 내 등불에 신경 써주어 고맙다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47603, 'ptBR', 'O misterioso e magnífico Al\'Abas', 'Fale com Al\'Abas.', 'Eu estou vendo, mortal insignificante! Obrigado por se preocupar com a minha lâmpada!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47603, 'ruRU', 'Таинственный и великолепный Аль\'Абас', 'Поговорите с Аль\'Абасом.', 'Я вижу тебя, $gжалкий смертный:жалкая смертная;! Отрадно, что ты так печешься о моей лампе!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47603, 'zhCN', '神秘而伟大的阿尔阿巴斯', '与阿尔阿巴斯谈一谈。', '我看见你了，渺小的凡人！感谢你照顾我的灯！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47603, 'zhTW', '神秘而偉大的艾拉巴斯', '跟艾拉巴斯交談。', '渺小的凡人，我看到你了！感謝你對這盞燈如此關愛！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47604, 'deDE', 'Im Land der Magie', 'Sprecht mit Al\'Abas... auf ein Neues.', 'Ich meine, das Problem zu erkennen. Eure jämmerlichen Äuglein sind zu schwach, um die manaerfüllten Kostbarkeiten wahrzunehmen, die diese Elfen im ganzen Land verteilt haben. Sie tragen den Schmuck schon die ganze Zeit, aber Ihr konntet ihn einfach nicht sehen.\n\nIch schlage Euch ein Bündnis vor. Meine Lampe leidet derzeit unter einem peinlichen Mangel an Magie und könnte noch mehr edlere Verzierungen vertragen. Deshalb will ich Euch einen Handel anbieten. Ich durchtrenne diesen sinistren Schleier und Ihr überlasst mir die Beute Eures Diebstahls.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47604, 'esES', 'En tierra de magia', 'Habla con Al\'Abas... otra vez.', 'Creo que ya entiendo el problema. Tus ojillos son demasiado débiles para detectar las galas imbuidas de maná que estos elfos han propagado por esta tierra. Siempre han llevado joyas, pero simplemente no podías verlas.\n\nTe propongo una colaboración, mi $gnuevo socio:nueva socia;. Mi candil está vergonzosamente falto de magia y podría mejorarse con adornos más bonitos, así que hagamos un trato: yo desvelo este siniestro misterio y tú me das el botín que obtengas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47604, 'esMX', 'En la tierra mágica', 'Habla con Al\'Abas... de nuevo.', 'Creo que veo el problema. Tus simples ojos son demasiado débiles para detectar el arte revestido de maná que estos elfos han propagado por la tierra. Han estado usando joyas todo el tiempo, y tú no pudiste notarlo.\n\nTe propongo una sociedad, mi recién $Gacuñado socio:acuñada socia;. Me temo que mi lámpara sufre de una terrible ausencia de magia y siempre puede beneficiarse con decoraciones adicionales. Te propongo un intercambio. Yo perforaré este siniestro velo y tú me entregarás los despojos de tus robos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47604, 'frFR', 'Au pays de la magie', 'Parlez à Al’Abas… encore.', 'Je pense savoir d’où vient le problème. Vos yeux sont bien trop ordinaires pour détecter les merveilles imprégnées de mana que ces Elfes ont éparpillés un peu partout sur ces terres. Tout ce temps, ils portaient de magnifiques bijoux et vous ne pouviez tout simplement pas les voir.\n\nEt si nous nous associions ? Ma lampe aurait bien besoin de quelques décorations supplémentaires pour résoudre un embarrassant problème de manque de magie. Je vous propose un marché : je percerai ce voile sinistre en échange du produit de votre larcin.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47604, 'itIT', 'Nella terra della magia', 'Parla con Al\'abas... ancora.', 'Credo di aver capito il problema. I tuoi miseri occhi sono troppo deboli per notare i preziosi fregi infusi di mana che gli Elfi hanno diffuso su questa terra. Hanno indossato gioielli per tutto questo tempo e tu non ti sei mai $gaccorto:accorta; di nulla.\n\nTi propongo un accordo. La mia lampada soffre attualmente di un\'imbarazzante carenza di magia e avrebbe bisogno di alcune nuove decorazioni. Potremmo fare uno scambio. Spezzerò questo sinistro velo e tu in cambio mi darai tutta la refurtiva.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47604, 'koKR', '마법의 땅에서', '알아바스와... 다시 대화해야 합니다.', '문제가 무엇인지 알 것 같다. 너의 시력이 너무나 미약하여 엘프들이 이 땅에 보급한 마나가 깃든 장신구를 감지할 수 없는 것이다. 사람들은 보석 장신구를 늘 차고 있었지만, 그게 너의 눈에는 보이지 않았던 것이지.\n\n나와 손을 잡자, 새 동료여. 지금 내 등불에 마법이 모자라 아주 난처한 상황이다. 그래서 아름다운 보석 장식은 언제나 환영이지. 자, 거래를 제안한다. 이 몹쓸 장막을 걷어줄 테니, 보석을 훔쳐와라.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47604, 'ptBR', 'Na terra da magia', 'Fale com Al\'Abas... de novo.', 'Acho que sei qual é o problema. Seus olhinhos mirrados são fracos demais para captar os ornatos imbuídos de mana que os elfos espalharam pela região. Eles estavam usando joias o tempo todo, mas você simplesmente não conseguia ver.\n\nTenho uma proposta para lhe fazer, $Gmeu mais novo parceiro:minha mais nova parceira;. Minha lâmpada no momento sofre de uma embaraçosa escassez de magia, e sempre pode se beneficiar de mais belos adornos. Proponho uma troca. Eu dissipo o véu sinistro, e você me repassa os espólios do roubo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47604, 'ruRU', 'В волшебной стране', 'Поговорите с Аль\'Абасом... еще раз.', 'Кажется, я понял, в чем дело. Твои слабые глаза не в состоянии увидеть наполненные маной побрякушки у эльфов по всему острову. Да-да, все это время они носили украшения, просто ты их не замечаешь.\n\nПредлагаю тебе сделку, $gмой новоиспеченный подельник:моя новоиспеченная подельница;. Моей лампе не хватает вливаний магии, да и новые украшения не помешают. Давай я приподниму завесу, а ты поделишься со мной добычей.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47604, 'zhCN', '魔法之地', '再……跟阿尔阿巴斯谈谈吧。', '我懂了。你那双小眼睛基本上就是个摆设，看不到那些精灵散布在这片土地各处的灌魔装饰品。他们一直都佩戴着珠宝，而你却看不见。 \n\n我提议我们合作，我的新朋友。我的灯目前正缺漂亮的装饰，实在是太过寒酸。我提议我们来做笔交易。由我来揭开这道阴险的面纱，而你则要把找到的宝物交给我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47604, 'zhTW', '魔法之地', '再跟艾拉巴斯交談一次。', '我知道問題在哪裡了。你的小眼睛太弱，看不見這些精靈散落在各處的法力附魔精品。精靈們一直都佩戴著珠寶，只是你看不見。\n\n親愛的新夥伴，我們來合作吧！我的油燈現在嚴重缺乏魔法，而且我一直想要更漂亮的裝飾。我們可以做個交易。我負責揭開邪惡的真面目，然後你把偷到的東西交給我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47605, 'deDE', 'Münzen der Luft', 'Sprecht abermals mit Al\'Abas.', 'Ich hege größtes Interesse an dem magischen Geschmeide, das in den Taschen der Bewohner dieser Lande zu finden ist. Demgemäß biete ich bei der Beschaffung ebenjener untertänigst meine Dienste an, $n.\n\nZu meinem Unglück vermag ich nicht, Eure Mühen im hergebrachten Obolus dieser Welt zu vergelten. Was ich offerieren kann, sind Ephemera – Münzen von reinster Luft. \"Besser als nichts\", wie Ihr so schön sagt, nicht?\n\nTragt meine Lampe mit Euch. Verstaut Eure gestohlenen Schätze darin und ich werde sie mit diesen Münzen aufwiegen. Der einstige Besitzer meiner Lampe hat womöglich eine Verwendung dafür.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47605, 'esES', 'Monedas de aire', 'Habla con Al\'Abas una vez más.', 'Me interesan mucho esos abalorios mágicos que residen en los bolsillos de los moradores de esta tierra, y ofrezco mis humildes servicios para ayudarte a adquirirlos, $n.\n\nNo obstante, me temo que no puedo pagarte con la moneda de este reino. Lo que puedo ofrecerte es efímero: monedas de aire puro. ¡Son mejor que nada!\n\nLlévate mi candil. Mete ahí dentro los tesoros que robes y, a cambio, te entregaré estas monedas. Tal vez el antiguo dueño de mi candil pueda darles uso.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47605, 'esMX', 'Monedas de aire', 'Habla con Al\'Abas una vez más.', 'Estoy muy interesado en estos objetos mágicos que los ciudadanos de esta tierra llevan en los bolsillos, y humildemente te ofrezco mis servicios para ayudarte a obtenerlos, $n. \n\nSin embargo, me temo que no puedo pagarte con las monedas de este reino. Lo que puedo ofrecerte es algo más efímero... monedas del aire más puro. ¡Pero peor es nada!\n\nLleva mi lámpara contigo. Deja los tesoros que robes en su interior y yo te concederé estas monedas. Quizás el dueño anterior de mi lámpara pueda darles algún uso.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47605, 'frFR', 'Pièces d’air', 'Parlez à Al’Abas une fois de plus.', 'J’éprouve un intérêt tout particulier pour les babioles magiques nichées dans les poches des habitants de ces terres. Et je me tiens à votre entière disposition pour mettre la main dessus, $n.\n\nÀ mon grand regret, je ne pourrai honorer vos efforts avec la monnaie de ce royaume. Je vous propose toutefois des pièces d’air pur. Cela vaut toujours mieux que rien, n’est-ce pas ?\n\nPrenez ma lampe avec vous, glissez-y les trésors que vous subtiliserez et les pièces couleront à flot. À ce propos, elles intéresseront peut-être le précédent détenteur de ma lampe.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47605, 'itIT', 'Monete d\'Aria', 'Parla con Al\'abas ancora una volta.', 'Sarei particolarmente interessato a quegli oggetti magici che si trovano nelle tasche degli abitanti di questa terra e intendo offrirti umilmente il mio aiuto per recuperarli.\n\nDetto questo, purtroppo non posso pagarti con la moneta di questo reame. Quello che posso offrirti sono effimere monete di pura aria. Meglio di niente, dai!\n\nPorta con te la mia lampada, metti al suo interno tutti i tesori che riesci a rubare e in cambio ti ricompenserò con queste monete. Forse, il vecchio proprietario della mia lampada saprebbe farne buon uso.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47605, 'koKR', '바람의 주화', '또다시 알아바스와 대화해야 합니다.', '이 땅에 사는 주민들이 주머니에 넣어놓은 마법의 장신구가 아주 마음에 든다. 네가 그걸 손에 넣는 데 내 누추한 힘을 보태겠다, $n.\n\n그나저나, 나는 이 세계에서 쓰는 주화로 보상해 줄 수가 없다. 내가 줄 수 있는 건 금방 사라지는 순수한 바람의 주화다. 그래도 아무것도 없는 것보다는 낫지 않나!\n\n내 등불을 들고 가라. 훔친 보석을 여기에 넣어 오면 이 주화를 주도록 하겠다. 내 등불의 전 주인이라면, 어쩌면 그 주화를 필요로 할지도 모른다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47605, 'ptBR', 'Moedas de vento', 'Fale com Al\'Abas mais uma vez.', 'Eu tenho grande interesse nos berloques mágicos que jazem nos bolsos dos habitantes desta terra e ofereço humildemente meus serviços para ajudar você a pôr as mãos neles, $n.\n\nNo entanto, infelizmente eu não posso lhe pagar na moeda deste reino. Mas posso oferecer algo efêmero – moedas de puro ar... que são melhores que nada!\n\nLeve minha lâmpada com você. Solte os tesouros que roubar dentro dela e eu lhe darei as moedas. Talvez o antigo dono da lâmpada tenha uma utilidade para elas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47605, 'ruRU', 'Монеты воздуха', 'Поговорите с Аль\'Абасом еще раз.', 'Мне очень пригодятся волшебные побрякушки, которые жители этих земель носят в карманах, потому я и помогу тебе их заполучить, $n.\n\nВот только заплатить монетами этого мира я не смогу. Моя плата может быть эфемерной. Хочешь монеты из чистого воздуха? Все лучше, чем ничего!\n\nВозьми с собой мою лампу и бросай в нее все собранные сокровища, а я взамен дам тебе эти монеты. Возможно, бывший владелец лампы придумает, что с ними делать.\n', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47605, 'zhCN', '空气硬币', '再跟阿尔阿巴斯谈一谈。', '我对这里的居民口袋里的那些魔法小玩意儿很感兴趣，而且我愿意帮助你获得它们，$n。 \n\n可惜，我无法用这个世界的货币来报答你。我只能给你一种临时性的货币——由纯净的空气构成的硬币。但这总比啥都没有强！\n\n将我的灯带在身边。将你偷来的宝贝丢进来，我就会给你硬币。或许我这盏灯的前主人会用得着它们。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47605, 'zhTW', '浮雲之幣', '再與艾拉巴斯交談一次。', '我對這些原本在此地居民口袋裡的魔法飾品很感興趣，也很願意提供協助，幫你取得這些東西，$n。 \n\n不過，很抱歉我沒辦法用這個世界通行的貨幣支付款項。我能給你的是稍縱即逝，浮雲般的貨幣。不管怎樣都比沒有好，對吧！\n\n帶著我的油燈。放下你在裡面偷到的寶物，我就會把貨幣給你。說不定油燈的前任主人會用得著。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47606, 'deDE', 'Treue ist eine Belohnung in sich', 'Erlangt 1000 Münzen der Luft.', 'Ich biete Euch eine Herausforderung, sterbliches Wesen. Erlangt eintausend meiner Münzen und ich bin zuversichtlich, die seherische Macht meiner Lampe verstärken zu können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47606, 'esES', 'La lealtad es una recompensa en sí', 'Obtén mil monedas de aire.', 'Te planteo un desafío, mortal: obtén mil de mis monedas y potenciaré mi candil y su visión interior.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47606, 'esMX', 'La lealtad es una recompensa en sí misma', 'Consigue mil monedas de aire.', 'Te ofrezco un desafío, mortal. Si consigues mil de mis monedas, creo que podré potenciar mi lámpara y la visión en su interior.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47606, 'frFR', 'La loyauté n’a pas de prix', 'Obtenez 1 000 Pièces d’air.', 'Je vous propose un défi, $gmortel:mortelle;. Obtenez un millier de mes pièces et il me sera possible de renforcer la vision conférée par ma lampe.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47606, 'itIT', 'La lealtà ha i suoi vantaggi', 'Ottieni mille Monete d\'Aria.', 'Ti offro una sfida, mortale. Ottieni mille delle mie monete e dovrei riuscire a potenziare la mia lampada e la visione al suo interno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47606, 'koKR', '충성은 그 자체로 의미 있는 법', '바람의 주화 1000개를 입수해야 합니다.', '도전 과제를 주지, 필멸자여. 바람의 주화를 1000개 모아라. 그러면 내 등불과 주위를 보는 힘을 강화할 수 있을 것이다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47606, 'ptBR', 'A lealdade já é uma recompensa', 'Obtenha mil Moedas de Vento.', 'Eu lhe ofereço um desafio, mortal. Acredito que se você obtiver mil das minhas moedas, eu poderei fortalecer minha lâmpada e a visão propiciada por ela.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47606, 'ruRU', 'Верность – сама по себе награда', 'Получите тысячу монет воздуха.', 'Выполнишь мое поручение, $gсмертный:смертная;? Заполучи тысячу монет, и с ними, уверен, я смогу сделать сильнее и лампу, и видение.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47606, 'zhCN', '忠诚就是最好的奖赏', '获得一千枚空气硬币。', '我有一个挑战要交给你，凡人。从我这里获得一千枚硬币。我相信到时候，我就能强化我的灯以及里面的幻象了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47606, 'zhTW', '忠誠本身就是獎勵', '獲得一千枚浮雲之幣。', '這是給你的挑戰，凡人。只要得到一千枚我給的貨幣，我就能強化這盞燈，還有裡面的幻象。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47607, 'deDE', 'Dschinn mit Modesinn', 'Durchsucht die Taschen der Bewohner der Verheerten Inseln nach seltenen Schätzen.', 'Durch meine noch mächtigere Lampe spüre ich eine viel größere Fülle an kostbarem Geschmeide in den Taschen dieser Elfen und Dämonen. Beglückt mich mit ihrem Besitz!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47607, 'esES', 'Genio y figura', 'Busca tesoros poco comunes en los bolsillos de los moradores de las Islas Abruptas.', 'Con mi recién potenciado candil, detecto abalorios más valiosos y lejanos en los bolsillos de elfos y demonios. ¡Consíguemelos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47607, 'esMX', 'Esos djinns lucen bastante caros', 'Busca tesoros raros en los bolsillos de los ciudadanos de las Islas Quebradas.', 'Ahora que mi lámpara está potenciada, presiento objetos más valiosos en los bolsillos de estos elfos y demonios. ¡Ve a buscarlos por mí!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47607, 'frFR', 'Le génie de la confection', 'Faites les poches des habitants des îles Brisées et trouvez-y de rares trésors.', 'Grâce à ma lampe désormais toute-puissante, ma vision s’est étendue. Je ressens la présence de colifichets d’une grande valeur dans les proches de ces Elfes et de ces démons. Rapportez-les-moi !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47607, 'itIT', 'Djinn d\'alta classe', 'Trova i tesori rari nelle tasche dei cittadini delle Isole Disperse.', 'Con la mia nuova lampada potenziata, percepisco la presenza di altri monili preziosi nelle tasche di quegli Elfi e demoni. Recuperali per me!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47607, 'koKR', '신령의 최신 유행 스타일', '부서진 섬 주민들의 주머니에 있는 희귀한 보물을 찾아야 합니다.', '이렇게 등불이 강화되고 나니, 엘프와 악마들 주머니에 들어 있는 더욱 진귀한 장신구들이 느껴진다. 내게 가져와라!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47607, 'ptBR', 'Djinn do mais alto nível', 'Encontre tesouros raros nos bolsos dos habitantes das Ilhas Partidas.', 'Com minha nova lâmpada fortalecida, eu sinto mais. Berloques ainda mais valiosos nos bolsos desses elfos e demônios. Traga todos para mim!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47607, 'ruRU', 'Джинн, прет-а-порте', 'Найдите редкие сокровища в карманах жителей Расколотых островов.', 'Теперь, когда моя лампа стала еще лучше, я ощущаю более могущественные предметы в карманах эльфов и демонов. Принеси их мне!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47607, 'zhCN', '奢华灯神', '在破碎群岛居民的口袋中找到稀有的宝物。', '有了我刚刚强化的灯，我能感应到距离更远、更加珍贵的物品了。它们就在这些精灵和恶魔的口袋里。去把它们给我弄来！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47607, 'zhTW', '華麗的神燈精靈', '在破碎群島居民口袋裡找到稀有的寶物。', '從我剛強化過的油燈可以進一步感覺到這些精靈和惡魔身上有更具價值的飾品。去幫我找回來！', '', '', '', '', '', '', 0);
