INSERT INTO `quest_template_locale` VALUES (48300, 'deDE', 'Der Seelenjäger', 'Besiegt Imonar den Seelenjäger in Antorus, dem Brennenden Thron.', 'Imonar ist der tödlichste Kopfgeldjäger, der je dem Wirbelnder Nether entsprungen ist. Sucht ein tiefes Loch, in das Ihr ihn stürzen könnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48300, 'esES', 'El Cazador de Almas', 'Derrota a Imonar Cazador de Almas en Antorus, el Trono Ardiente.', 'Imonar es el cazarrecompensas más letal que jamás haya salido del Vacío Abisal. Busca un foso al cual arrojarlo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48300, 'esMX', 'El cazador de almas', 'Derrota al Cazador de almas Imonar en Antorus, el Trono Ardiente.', 'Imonar es el cazador de recompensas más letal que haya creado el Vacío Abisal. Busca una fosa donde enterrarlo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48300, 'frFR', 'Le Chasseur d’âmes', 'Terrassez Imonar le Chasseur d’âmes à Antorus, le Trône ardent.', 'Imonar est le chasseur de primes le plus meurtrier jamais surgi du Néant distordu. Trouvez une fosse où le faire disparaître.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48300, 'itIT', 'Il Cacciatore d\'Anime', 'Sconfiggi Imonar il Cacciatore d\'Anime ad Antorus, il Trono Infuocato.', 'Imonar è il più letale cacciatore di taglie mai uscito dalla Distorsione Fatua. Trova una fossa e gettalo dentro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48300, 'koKR', '영혼사냥꾼', '안토러스 - 불타는 왕좌에서 영혼사냥꾼 이모나르를 처치해야 합니다.', '이모나르는 뒤틀린 황천이 만들어 낸 가장 무시무시한 현상금 사냥꾼입니다. 구덩이를 파고 놈을 던져 넣으십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48300, 'ptBR', 'O caçador de almas', 'Derrotar Imonar, o Caçador de Almas, em Antorus, o Trono Ardente.', 'Imonar é o caçador de recompensas mais perigoso que já saiu da Espiral Etérea. Vá sacudir o corpo dele numa cova.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48300, 'ruRU', 'Ловец душ', 'Победите ловца душ Имонара в Анторусе, Пылающем Троне.', 'Имонар – самый опасный охотник за головами из всех порождений Круговерти Пустоты. Нужно избавиться от него.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48300, 'zhCN', '猎魂者', '击败安托鲁斯，燃烧王座的猎魂者伊墨纳尔。', '伊墨纳尔是扭曲虚空中有史以来最致命的赏金猎人。找个地方把他解决掉。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48300, 'zhTW', '獵魂者', '在安托洛斯，燃燒王座擊敗『獵魂者』伊莫納爾。', '伊莫納爾是扭曲虛空最為危險致命的賞金獵人。找個坑洞把它扔進去。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48301, 'deDE', 'Der Brennende Zirkel', 'Bezwingt den Zirkel der Shivarra in Antorus, dem Brennenden Thron.', 'Nicht einmal ein Titan könnte ihre Folter ertragen. Setzt dem Zirkel der Shivarra ein Ende.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48301, 'esES', 'El aquelarre ardiente', 'Derrota al Aquelarre shivarra en Antorus, el Trono Ardiente.', 'Ni siquiera un titán puede resistir su tortura. Acaba con el Aquelarre shivarra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48301, 'esMX', 'El aquelarre ardiente', 'Derrota al Aquelarre de las shivarra en Antorus, el Trono Ardiente.', 'Ni siquiera un titán puede soportar esa tortura. Acaba con el Aquelarre de las shivarra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48301, 'frFR', 'Le Sabbat ardent', 'Éliminez le Sabbat des shivarra à Antorus, le Trône ardent.', 'Même un Titan ne saurait survivre aux effroyables actes de torture du Sabbat des shivarra. Mettez un terme à ses méfaits.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48301, 'itIT', 'La congrega infuocata', 'Sconfiggi la Congrega delle Shivarra ad Antorus, il Trono Infuocato.', 'Nemmeno un Titano potrebbe resistere alle loro torture. Distruggi la Congrega delle Shivarra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48301, 'koKR', '불타는 집회', '안토러스 - 불타는 왕좌에서 쉬바라의 집회를 처치해야 합니다.', '아무리 티탄이라 한들 놈들의 고문을 견뎌낼 수는 없습니다. 쉬바라의 집회에 최후를 안겨 주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48301, 'ptBR', 'O conciliábulo ardente', 'Derrote o Conciliábulo de Shivarra em Antorus, o Trono Ardente.', 'Nem mesmo um Titã suporta a tortura delas. Ponha fim no Conciliábulo de Shivarra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48301, 'ruRU', 'Пылающий шабаш', 'Победите ковен шиварр в Анторусе, Пылающем Троне.', 'Даже титан не способен вынести такие пытки. Нужно покончить с этим ковеном шиварр.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48301, 'zhCN', '燃烧的女巫会', '击败安托鲁斯，燃烧王座的破坏魔女巫会。', '即使是泰坦也无法忍受他们的折磨。把破坏魔女巫会干掉吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48301, 'zhTW', '燃燒軍團巫女', '在安托洛斯，燃燒王座擊敗希瓦拉巫女。', '即使是泰坦，也無法承受這樣的折磨。消滅希瓦拉巫女，讓她們無法再作惡。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48302, 'deDE', 'Der Zerrütter', 'Bezwingt Argus, den Zerrütter, in Antorus, dem Brennenden Thron.', 'Argus ist Sargeras‘ Lösung für die Existenz. Ein Ende für alle Dinge. Setzt dem Zerrütter ein Ende, für all die, die je leben werden.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48302, 'esES', 'El Aniquilador', 'Derrota a Argus el Aniquilador en Antorus, el Trono Ardiente.', 'Argus es la solución de Sargeras para la existencia; un fin para todas las cosas. Por todos aquellos que algún día puedan vivir, acaba con el Aniquilador.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48302, 'esMX', 'El Asolador', 'Derrota a Argus el Asolador en Antorus, el Trono Ardiente.', 'Argus es la solución de Sargeras para la existencia, el fin de todas las cosas. Por toda la vida actual y futura, debes acabar con el Asolador.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48302, 'frFR', 'L’Annihilateur', 'Terrassez Argus l’Annihilateur à Antorus, le Trône ardent.', 'Avec Argus, Sargeras compte mettre fin au concept même d’existence. Terrassez l’Annihilateur, pour le salut de tous les vivants !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48302, 'itIT', 'Il Distruttore', 'Sconfiggi Argus il Distruttore ad Antorus, il Trono Infuocato.', 'Argus è la soluzione scelta da Sargeras per porre fine a tutte le cose. Per il bene della vita futura, elimina il Distruttore.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48302, 'koKR', '사멸자', '안토러스 - 불타는 왕좌에서 사멸자 아르거스를 처치해야 합니다.', '아르거스는 존재하는 모든 것들에 대한 살게라스의 대응책입니다. 바로 모든 것의 종말을 의미하지요. 살아 있는 모든 존재를 위하여, 사멸자에게 최후를 안겨 주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48302, 'ptBR', 'O descriador', 'Derrote Argus, o Descriador, em Antorus, o Trono Ardente.', 'Argus é a solução de Sargeras para a existência: o fim de tudo. Por todos os seres que podem vir a viver um dia, ponha fim no Descriador.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48302, 'ruRU', 'Аргус Порабощенный', 'Победите Аргуса Порабощенного в Анторусе, Пылающем Троне.', 'Аргус – это ключ ко всем замыслам Саргераса; он должен положить конец всему сущему. Ради жизни в этом мире и во всех других мирах надо покончить с Аргусом Порабощенным.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48302, 'zhCN', '寂灭者', '击败安托鲁斯，燃烧王座的寂灭者阿古斯。', '阿古斯是萨格拉斯消灭万物存在的终极手段。为了现在和将来的所有生命，请消灭寂灭者。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48302, 'zhTW', '滅界者', '在安托洛斯，燃燒王座擊敗『滅界者』阿古斯。', '阿古斯是薩格拉斯的存續之所依，也是萬千生靈禍滅之所倚。為了所有人生存下去的希望，一定要翦除滅界者。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48303, 'deDE', 'Das Antorische Oberkommando', 'Bezwingt das Antorische Oberkommando in Antorus, dem Brennenden Thron.', 'Die Legion hat über 10.000 Jahre damit verbracht, die Invasion von Azeroth zu planen. Besiegt ihre Anführer, damit sie nie wieder unsere Welt bedrohen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48303, 'esES', 'Alto Mando antoran', 'Derrota al Alto Mando antoran en Antorus, el Trono Ardiente.', 'La Legión ha dedicado más de 10 000 años a planear la invasión de Azeroth. Derrota a sus líderes para que nunca puedan volver a amenazar a nuestro mundo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48303, 'esMX', 'Alto mando antoriano', 'Derrota al Alto mando antoriano en Antorus, el Trono Ardiente.', 'La Legión ha pasado más de 10,000 años planeando su invasión sobre Azeroth. Derrota a sus líderes para que nunca más amenacen nuestro mundo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48303, 'frFR', 'Le haut-commandement antoréen', 'Éliminez le Haut-commandement antoréen à Antorus, le Trône ardent.', 'La Légion a passé plus de dix millénaires à préparer l’invasion d’Azeroth. Éliminez ses chefs afin qu’elle ne puisse plus jamais menacer notre monde.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48303, 'itIT', 'L\'Alto Comando degli Antoran', 'Sconfiggi l\'Alto Comando degli Antoran ad Antorus, il Trono Infuocato.', 'La Legione ha passato oltre 10.000 anni a pianificare l\'invasione di Azeroth. Sconfiggi i loro capi, così che non possano più minacciare il nostro mondo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48303, 'koKR', '안토란 총사령부', '안토러스 - 불타는 왕좌에서 안토란 총사령부를 처치해야 합니다.', '군단은 만 년이 넘도록 아제로스를 침략할 계획을 세웠습니다. 놈들이 다시는 우리 세계를 위협하지 못하도록 놈들의 지도부를 처치해 주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48303, 'ptBR', 'Alto Comando Antorano', 'Derrotar o Alto Comando Antorano em Antorus, o Trono Ardente.', 'A Legião passou 10.000 anos planejando a invasão a Azeroth. Derrote os líderes deles para que nunca voltem a ameaçar o nosso mundo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48303, 'ruRU', 'Военный совет Анторуса', 'Победите военный совет Анторуса в Анторусе, Пылающем Троне.', 'Легион планировал вторжение в Азерот более 10 000 лет. Нужно убить их командующих, чтобы навсегда избавить наш мир от этой угрозы.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48303, 'zhCN', '安托兰统帅议会', '击败安托鲁斯，燃烧王座的安托兰统帅议会。', '燃烧军团花费了一万多年的时间策划对艾泽拉斯的入侵。击败他们的首领，让他们再也无法威胁到我们的世界。', '', '', '', '', '', '', 0);
