INSERT INTO `quest_template_locale` VALUES (48306, 'itIT', 'Il Distruttore', 'Sconfiggi Argus il Distruttore ad Antorus, il Trono Infuocato.', 'Argus è la soluzione scelta da Sargeras per porre fine a tutte le cose. Per il bene della vita futura, elimina il Distruttore.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48306, 'koKR', '사멸자', '안토러스 - 불타는 왕좌에서 사멸자 아르거스를 처치해야 합니다.', '아르거스는 존재하는 모든 것들에 대한 살게라스의 대응책입니다. 바로 모든 것의 종말을 의미하지요. 살아 있는 모든 존재를 위하여, 사멸자에게 최후를 안겨 주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48306, 'ptBR', 'O descriador', 'Derrote Argus, o Descriador, em Antorus, o Trono Ardente.', 'Argus é a solução de Sargeras para a existência: o fim de tudo. Por todos os seres que podem vir a viver um dia, ponha fim no Descriador.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48306, 'ruRU', 'Аргус Порабощенный', 'Победите Аргуса Порабощенного в Анторусе, Пылающем Троне.', 'Аргус – это ключ ко всем замыслам Саргераса; он должен положить конец всему сущему. Ради жизни в этом мире и во всех других мирах надо покончить с Аргусом Порабощенным.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48306, 'zhCN', '寂灭者', '击败安托鲁斯，燃烧王座的寂灭者阿古斯。', '阿古斯是萨格拉斯消灭万物存在的终极手段。为了现在和将来的所有生命，请消灭寂灭者。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48306, 'zhTW', '滅界者', '在安托洛斯，燃燒王座擊敗『滅界者』阿古斯。', '阿古斯是薩格拉斯的存續之所依，也是萬千生靈禍滅之所倚。為了所有人生存下去的希望，一定要翦除滅界者。', '', '', '', '', '', '', 0);
