INSERT INTO `quest_template_locale` VALUES (48513, 'deDE', 'Invasionspunktoffensive', 'Schließt 3 Invasionspunkte ab.', 'An den Invasionspunkten sind wir in der Lage, die Legion auf Argus von ihrer Verstärkung abzuschneiden. Wenn wir ihre Truppen in den geringen Rissen besiegen, können wir größere Invasionspunkte angreifen und die Legion dort niederstrecken.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48513, 'esES', 'Ofensiva en punto de invasión', 'Completa 3 puntos de invasión.', 'Los puntos de invasión son clave para frustrar la llegada de refuerzos de la Legión a Argus. Derrotar a sus ejércitos en los portales inferiores nos permitirá asaltar puntos de invasión más grandes y extinguir la presencia de la Legión en ellos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48513, 'esMX', 'Ofensiva del punto de invasión', 'Completa 3 puntos de invasión.', 'Los puntos de invasión son clave para impedir que los refuerzos de la Legión ingresen en Argus. Si derrotas a sus tropas en los portales menos difíciles, podremos atacar puntos de invasión más grandes y acabar con la presencia de la Legión allí.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48513, 'frFR', 'Attaque du point d’invasion', 'Terminez 3 Sites d’invasion.', 'Les sites d’invasion sont cruciaux pour empêcher l’arrivée de renforts ennemis sur Argus. Vaincre les armées de la Légion dans les portails plus modestes nous permettra d’attaquer et anéantir des sites d’invasion majeurs.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48513, 'itIT', 'Offensiva al Punto di Invasione', 'Completa 3 Punti di Invasione.', 'I Punti di Invasione sono la chiave per tagliare i rifornimenti della Legione verso Argus. Sconfiggendo le armate nascoste all\'interno dei portali minori, potremmo attaccare Punti di Invasione più grandi e distruggere le forze della Legione al loro interno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48513, 'koKR', '침공 거점 공격', '침공 거점 3개를 완료해야 합니다.', '침공 거점은 군단이 아르거스로 병력을 보내는 길을 끊는 열쇠다. 소규모 차원문 너머의 병력을 쓰러뜨리면 더 큰 침공 거점을 공격하고 그곳의 군단을 전멸시킬 수 있을 것이다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48513, 'ptBR', 'Ofensiva do Ponto de Invasão', 'Complete 3 Pontos de Invasão.', 'Os Pontos de Invasão são a chave para cortar o envio de reforços da Legião para Argus. Derrote os exércitos deles nos portais menores para que possamos atacar Pontos de Invasão maiores e acabar com a presença da Legião nessas áreas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48513, 'ruRU', 'Атака точки вторжения', 'Пройдите 3 точки вторжения.', 'Зачищая точки вторжения, мы отрезаем Легион на Аргусе от подкреплений. Уничтожай войска и малые порталы противника, чтобы мы могли атаковать и другие точки вторжения и остановить Легион.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48513, 'zhCN', '攻击侵入点', '完成3个侵入点。', '侵入点是切断燃烧军团往阿古斯运送援军的关键。击败次级传送门里的军团部队能够让我们袭击更加大型的侵入点，并消灭那里的军团部队。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48513, 'zhTW', '攻擊侵略點', '完成3個侵略點。', '侵略點是防止燃燒軍團增援阿古斯的關鍵。進入小型傳送門擊敗它們的部隊，就可以讓我們進攻更大的侵略點，消滅其中的燃燒軍團勢力。', '', '', '', '', '', '', 0);
