INSERT INTO `quest_template_locale` VALUES (48654, 'koKR', '오로나아르 아래서', '오로나아르 아래에 있는 크로쿨과 대화해야 합니다.', '오로나아르 아래에서 발견한 크로쿨은 떠나려 하질 않더군요. 그와 얘기해 보실래요, $n 님? 그 크로쿨이 구원호에 타도록 설득해 주세요.', '', '', '', '', '', '', 0);
