INSERT INTO `quest_template_locale` VALUES (48654, 'ruRU', 'Под Оронааром', 'Поговорите с крокулом под Оронааром.', 'Крокул, которого мы нашли под Оранааром, не захотел покидать свою пещеру. Может, поговоришь с ним, $n? Уговори его отправиться на \"Виндикар\".', '', '', '', '', '', '', 0);
