INSERT INTO `quest_template_locale` VALUES (48973, 'frFR', 'Parangon du Front argusséen', 'Parlez à Toraan le Révéré à bord du Vindicaar.', 'Vos alliés du Front argusséen vous demandent. Ils souhaitent vous récompenser pour votre indéfectible soutien.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48973, 'itIT', 'Eccellenza dei Redentori di Argus', 'Parla con Toraan il Riverito sulla Vindicaar.', 'I tuoi alleati dei Redentori di Argus hanno richiesto la tua presenza. Desiderano ricompensarti per il tuo continuo aiuto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48973, 'koKR', '아르거스 세력단과 불멸의 동맹', '구원호에 있는 존경받는 토라안과 대화해야 합니다.', '아르거스 세력단 동맹군이 당신에게 방문해 줄 것을 요청했습니다. 지금껏 협력해 준 당신에게 보답을 하고 싶은 것 같습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48973, 'ptBR', 'Paragão da Casta Argussiana', 'Fale com Toraan, o Venerado, na Vindicaar.', 'Seus aliados da Casta Argussiana solicitam sua presença. Eles querem lhe recompensar pelos esforços contínuos em nome da causa deles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48973, 'ruRU', 'Образцовый Защитник Аргуса', 'Поговорите с Торааном Почитаемым на \"Виндикаре\".', 'Твои союзники из числа Защитников Аргуса просят о встрече. Они хотят вознаградить тебя за вклад в их дело.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48973, 'zhCN', '阿古斯防卫军的典范', '与维迪卡尔的可敬的托兰谈一谈。', '你在阿古斯防卫军的盟友请求你前去他们那里。他们想酬谢你对他们长久以来的帮助。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48973, 'zhTW', '阿古斯守望典範', '在梵迪卡爾與『崇敬者』托爾蘭交談。', '阿古斯守望的盟友對你鼎力相助十分感激，想獎勵你，請你過去一趟。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48974, 'deDE', 'Champion der Armee des Lichts', 'Sprecht mit Verteidigerin Jaelaana auf der Vindikaar.', 'Eure Verbündeten in der Armee des Lichts möchten Euch für Eure unermüdliche Unterstützung danken. Sie erwarten Euch auf der Vindikaar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48974, 'esES', 'Baluarte del Ejército de la Luz', 'Habla con la vindicadora Jaelaana en el Vindicaar.', 'A tus aliados en el Ejército de la Luz les gustaría agradecerte tu apoyo constante. Te esperan en el Vindicaar.', '', '', '', '', '', '', 0);
