INSERT INTO `quest_template_locale` VALUES (48974, 'frFR', 'Parangon de l’armée de la Lumière', 'Parlez à la Redresseuse de torts Jaelaana à bord du Vindicaar.', 'Vos alliés au sein de l’armée de la Lumière souhaitent vous récompenser pour votre indéfectible soutien. Ils vous attendent à bord du Vindicaar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48974, 'itIT', 'Eccellenza dell\'Armata della Luce', 'Parla con la Vendicatrice Jaelaana sulla Vindicaar.', 'I tuoi alleati nell\'Armata della Luce vorrebbero ringraziarti per il tuo continuo supporto. Ti stanno aspettando sulla Vindicaar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48974, 'koKR', '빛의 군대와 불멸의 동맹', '구원호에 있는 구원자 잴라아나와 대화해야 합니다.', '빛의 군대 동맹군이 당신의 끊임없는 노고에 감사를 표하고 싶어합니다. 구원호에서 기다리고 있다고 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48974, 'ptBR', 'Paragão do Exército da Luz', 'Fale com o Vindicante Jaelaana na Vindicaar.', 'Seus aliados no Exército da Luz gostariam de agradecer pelos seus esforços contínuos. Eles estão à espera na Vindicaar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48974, 'ruRU', 'Образцовый боец Армии Света', 'Поговорите с воздаятельницей Джелааной на \"Виндикаре\".', 'Твои союзники из Армии Света хотят отблагодарить тебя за помощь. Они ждут на \"Виндикаре\".', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48974, 'zhCN', '圣光军团的典范', '与维迪卡尔的守备官嘉兰娜谈一谈。', '你在圣光军团的盟友想感谢你长久以来对他们的帮助。他们正在维迪卡尔上等你。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48974, 'zhTW', '聖光軍團典範', '在梵迪卡爾上與復仇者潔拉娜交談。', '聖光軍團的盟友對你鼎力相助十分感激，想獎勵你，請你過去一趟。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48976, 'deDE', 'Vorräte des Argusvorstoßes', 'Sprecht mit Toraan dem Verehrten auf der Vindikaar.', '$ct, dank Eurer Bemühungen haben unsere Streitkräfte Vorräte entdeckt und sie davor bewahrt, dem Feind in die Hände zu fallen.\n\nDurch Eure fortwährende Unterstützung werden wir weitere Ressourcen anbieten können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48976, 'esES', 'Suministros de la Gloria de Argus', 'Habla con Toraan el Venerado en el Vindicaar.', '$ct, gracias a tus esfuerzos, nuestras tropas han podido encontrar recursos y evitar que caigan en manos de la Legión.\n\nCon tu ayuda, lograremos aportar recursos adicionales.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48976, 'esMX', 'Suministros del Dominio argusiano', 'Habla con Toraan, el Venerado en el Vindicaar.', '$ct, gracias a tus esfuerzos, nuestras fuerzas han sido capaces de ubicar recursos e impedir que cayeran en manos de la Legión.\n\nCon tu ayuda permanente, podremos proporcionar más recursos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48976, 'frFR', 'Fournitures du Front argusséen', 'Parlez à Toraan le Révéré à bord du Vindicaar.', '$ct, grâce à vos efforts, nos troupes ont pu localiser des ressources et empêcher qu’elles tombent entre les mains de la Légion.\n\nSi vous continuez de nous apporter votre aide, nous pourrons proposer des ressources supplémentaires.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48976, 'itIT', 'Rifornimenti dai Redentori di Argus', 'Parla con Toraan il Riverito sulla Vindicaar.', '$Ct, grazie al tuo aiuto le nostre forze sono state in grado di localizzare le risorse e impedire che cadessero nelle mani della Legione.\n\nCon il tuo supporto, riusciremo a trovarne altre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48976, 'koKR', '아르거스 세력단의 물자', '구원호에 있는 존경받는 토라안과 대화해야 합니다.', '$ct|1이여;여;, 당신이 애써준 덕에 우리 병력이 자원을 확보해 군단의 손에 들어가는 것을 막을 수 있었습니다.\n\n당신이 계속 도와준다면 우리 군대가 더 많은 자원을 제공할 수 있을 것입니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48976, 'ptBR', 'Suprimentos da Casta Argussiana', 'Fale com Toraan, o Venerado, na Vindicaar.', '$ct, graças à sua ajuda, nossas forças conseguiram encontrar recursos e impediram que eles caíssem nas mãos da Legião.\n\nCom você do nosso lado, teremos como prover ainda mais suprimentos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48976, 'ruRU', 'Припасы от Защитников Аргуса', 'Поговорите с Торааном Почитаемым на борту \"Виндикара\".', '$ct, благодаря тебе воины сумели отыскать наши запасы и спасти их от Легиона.\n\nЕсли ты будешь помогать нам и дальше, мы сможем предоставить тебе дополнительные ресурсы.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48976, 'zhCN', '阿古斯防卫军的物资', '与维迪卡尔的可敬的托兰谈一谈。', '$ct，多亏了你的努力，我们的军队从战场上成功找到了这些资源，没让它们落入燃烧军团的手中。\n\n只要你继续帮助我们，我们就可以提供额外的资源。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48976, 'zhTW', '阿古斯守望的補給品', '在梵迪卡爾與『崇敬者』托爾蘭交談。', '$ct，因為你努力不懈，讓我們的部隊能搶先找到資源，使資源不致落入燃燒軍團手中。\n\n只要你繼續幫助我們，我們就能給你更多資源。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48977, 'deDE', 'Vorräte der Armee des Lichts', 'Sprecht mit Verteidigerin Jaelaana auf der Vindikaar, um Eure Belohnung zu erhalten.', 'Archenon poros, Champion.\n\nDie Armee des Lichts möchte Euch für Eure Hilfe im Kampf gegen die Legion auf Argus danken.\n\nBitte nehmt diese Vorräte zusammen mit unserer tiefen Dankbarkeit an. Mit Eurer unermüdlichen Unterstützung kommen wir unserem Ziel, die Legion ein für alle Mal zu vernichten, jeden Tag etwas näher.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48977, 'esES', 'Suministros del Ejército de la Luz', 'Habla con la vindicadora Jaelaana en el Vindicaar para recibir tu recompensa.', 'Archenon poros, $gcampeón:campeona;.\n\nEl Ejército de la Luz desea darte las gracias por tu ayuda contra las tropas de la Legión en Argus.\n\nTe ruego que aceptes estos suministros con nuestra gratitud. Tu apoyo constante cada vez nos acerca más al objetivo final de erradicar a la Legión de una vez por todas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48977, 'esMX', 'Suministros del Ejército de la Luz', 'Habla con la Vindicadora Jaelaana en el Vindicaar para recibir tu recompensa.', 'Archenon poros, $Gcampeón:campeona;.\n\nEl Ejército de la Luz quiere agradecerte por la ayuda que nos brindaste contra las fuerzas de la Legión en Argus.\n\nAcepta estos suministros como muestra de nuestro agradecimiento. Gracias a tu apoyo constante, estamos más cerca que nunca de alcanzar nuestro objetivo final de exterminar a la Legión para siempre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48977, 'frFR', 'Fournitures de l’armée de la Lumière', 'Parlez à la Redresseuse de torts Jaelaana à bord du Vindicaar pour obtenir votre récompense.', 'Archenon poros, $gchampion:championne;.\n\nL’armée de la Lumière souhaite vous remercier pour votre aide dans notre combat contre les armées de la Légion présentes sur Argus.\n\nVeuillez accepter ces fournitures en gage de notre gratitude. C’est votre soutien sans faille qui nous permet de nous rapprocher de notre objectif ultime, l’éradication complète et définitive de la Légion.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48977, 'itIT', 'Rifornimenti dall\'Armata della Luce', 'Parla con la Vendicatrice Jaelaana sulla Vindicaar per ottenere la tua ricompensa.', 'Archenon poros, $gcampione:campionessa;.\n\nL\'Armata della Luce desidera ringraziarti per l\'aiuto che hai dato contro le forze della Legione su Argus.\n\nAccetta questi, con i nostri ringraziamenti. Il tuo continuo sostegno ci porta sempre più vicini alla distruzione finale della Legione.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48977, 'koKR', '빛의 군대의 물자', '구원호에 있는 구원자 잴라아나와 대화해서 보상을 받아야 합니다.', '아르케논 포로스, 영웅이여.\n\n빛의 군대는 아르거스에 있는 군단 병력에 대항해 주신 당신의 노고에 감사를 드리고 싶습니다.\n\n감사의 표시로 이 보관함을 드리니 부디 받아주십시오. 당신의 끊임없는 지원 덕분에 군단을 영원히 몰아내려는 저희의 궁극적인 목표에 어느 때보다 가까이 다가갔습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48977, 'ptBR', 'Suprimentos do Exército da Luz', 'Fale com o Vindicante Jaelaana na Vindicaar para pegar a sua recompensa.', 'Arkenon poros, campe$Gão:ã;.\n\nO Exército da Luz gostaria de agradecer por toda a ajuda na luta contra as forças da Legião em Argus.\n\nAqui trazemos alguns suprimentos como prova da nossa gratidão. Graças ao teu apoio de hoje e sempre, estamos mais perto do nosso objetivo: exterminar a Legião de uma vez por todas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48977, 'ruRU', 'Припасы от Армии Света', 'Поговорите с воздаятельницей Джелааной на \"Виндикаре\", чтобы получить награду.', 'Аркенон порос, $gзащитник:защитница;.\n\nАрмия Света желает отблагодарить тебя за помощь в борьбе с Легионом на Аргусе.\n\nПрими это в знак нашей признательности. Благодаря твоей поддержке мы как никогда близки к нашей заветной цели – покончить с Легионом раз и навсегда.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48977, 'zhCN', '圣光军团的物资', '与维迪卡尔的守备官嘉兰娜谈一谈，领取你的奖励。', 'Archenon poros，勇士。\n\n圣光军团希望感谢你帮忙对抗燃烧军团在阿古斯的部队。\n\n这些物资代表着我们的谢意，请收下吧。在你的不懈努力下，我们距离终极目标铲除军团又近了一步。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (48977, 'zhTW', '聖光軍團的補給品', '在梵迪卡爾上與復仇者潔拉娜交談，以取得你的獎勵。', '勇士，archenon poros (祝你好運)。\n\n聖光軍團想感謝你在阿古斯幫忙對抗燃燒軍團。\n\n我們非常感激，請接受這些補給品。因為你一直支持聖光軍團，我們才能繼續努力，總有一天我們會能永遠消滅燃燒軍團的。', '', '', '', '', '', '', 0);
