INSERT INTO `quest_template_locale` VALUES (49076, 'zhCN', '安托鲁斯，燃烧王座：黑暗通道', '从安托鲁斯，燃烧王座的猎魂者伊墨纳尔身上收集4枚通行符文（史诗难度）。', '尽管阿古斯的核心一直在喷发危险的能量，猎魂者依然可以在燃烧的王座中随意走动。根据斥候的报告，他的舰船利用恶魔符文的力量安全抵达邪能圣堂。\n\n如果我们能够拿到那些符文，维迪卡尔号就可以采取同样的航线了。这能为我们的部队节省宝贵的时间。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49076, 'zhTW', '安托洛斯，燃燒王座：黑暗通道', '在傳奇難度的安托洛斯，燃燒王座中，從『獵魂者』伊莫納爾身上獲得4個通行符文。', '雖然阿古斯的核心不斷發出危險的能量，獵魂者還是可以在燃燒王座來去自如。根據我們的偵察報告，他的船艦會利用惡魔符文通往聖禦魔城。\n\n如果我們能拿到那些惡魔符文，梵迪卡爾就可以走同樣的路線，這樣一來就能替我們的部隊省下寶貴的時間。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49077, 'deDE', 'Momente der Reue', 'Bringt Illidans Kristall zu den Personen und Orten, die ihm am meisten am Herzen liegen.', '<Der Kristall ruft Euch und vibriert vor Magie.\n\nAls Ihr ihn anfasst, hört Ihr das Echo einer vergangenen Nachricht, eine Aufgabe, die Illidan Euch anvertraut hat.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49077, 'esES', 'Momentos de reflexión', 'Llévale el cristal de Illidan a su gente y sus lugares más cercanos.', '<El cristal te llama y palpita con magia embrujada.\n\nAl cogerlo, oyes el eco de un mensaje que ha quedado en el pasado, una tarea que Illidan te ha confiado.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49077, 'esMX', 'Momentos de reflexión', 'Lleva el cristal de Illidan a las personas y los lugares más allegados a él.', '<La energía mágica del cristal te llama.\n\nCuando lo tienes en tus manos, oyes el eco de un mensaje del pasado, una tarea que Illidan te ha confiado.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49077, 'frFR', 'Moments de réflexion', 'Apportez le Cristal d’Illidan aux personnes et aux endroits qui lui sont chers.', '<La magie ensorceleuse qui émane du cristal vous attire.\n\nQuand vous vous en emparez, vous entendez l’écho du message qu’il contient, une tâche qu’Illidan vous a confiée.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49077, 'itIT', 'Momenti di riflessione', 'Porta il cristallo di Illidan alle persone e nei luoghi a lui più cari.', '<Il cristallo ti chiama, risuonando di energia magica.>\n\n<Appena lo afferri, percepisci l\'eco di un messaggio nascosto all\'interno, un compito che Illidan ti ha affidato.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49077, 'koKR', '숙고의 시간', '일리단의 수정을 그와 가까운 사람들과 장소로 가져가야 합니다.', '<마법의 힘을 내뿜는 수정이 당신을 부릅니다.\n\n수정을 움켜쥐자 일리단이 당신을 믿고 남겨 놓은 전언이 들려옵니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49077, 'ptBR', 'Momentos de reflexão', 'Leve o cristal de Illidan às pessoas e lugares mais próximos dele.', '<O cristal chama o seu nome, emanando energia mágica.\n\nAo tomá-lo nas mãos, você ouve o eco de uma mensagem deixada para trás, um dever confiado a você por Illidan.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49077, 'ruRU', 'Минуты раздумий', 'Отнесите кристалл Иллидана в те места, что хранили память о нем, и к тем, кто был для него дороже всего.', '<Кристалл взывает к вам, излучая магическую энергию.\n\nКогда вы берете его в руки, то слышите отзвук сообщения, которое оставил для вас Иллидан. Вы должны выполнить его поручение.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49077, 'zhCN', '反思之时', '带着伊利丹的水晶去找他最亲近的人和最熟悉的地方。', '<这块水晶召唤着你，它正在与巫术魔法共鸣。\n\n当你握住它时，听到了里面留下的信息，那是伊利丹交给你的任务。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (49077, 'zhTW', '回憶時刻', '把伊利丹的水晶交給他最親近的人，然後再帶去影響他最深的地方。', '<水晶對你發出呼喚，散發著巫士魔法的能量。\n\n握在手中的時候，你聽見了迴盪在其中的訊息，訴說著伊利丹託付給你的任務。>', '', '', '', '', '', '', 0);
