INSERT INTO `quest_template_locale` VALUES (60004, 'zhTW', '英勇之舉', '到訓練場救助$1oa個受傷的驍士。', '天譴軍團許久沒有以如此大的陣仗攻打過來。我們的力量不足以抵擋此等規模的突擊。\n\n雖然我們護送許多人前往安全處，還是有人在混亂中受傷了。\n\n搜尋訓練場，找出混戰的倖存者。\n\n我們需要每一分助力。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60005, 'deDE', 'Gefahr im Verzug', 'Sucht Thanikos in Xandrias Wacht auf.', 'Der Tempel des Mutes darf nicht fallen!\n\nAlle Kyrianer lernen in seinen heiligen Hallen das Kämpfen. Nicht nur, um die Bastion zu beschützen, sondern auch, um ihre eigene Sicherheit zu garantieren, wenn sie Seelen durch den Schleier holen.\n\nReist umgehend dorthin. Sucht Thanikos auf, die treue Hand des Paragons des Mutes.', '', '', '', '', '', 'Sucht Thanikos in Xandrias Wacht auf.', 0);
INSERT INTO `quest_template_locale` VALUES (60005, 'esES', 'Peligro inminente', 'Busca a Thanikos en la Vigilia de Xandria.', '¡El Templo del Coraje no debe caer!\n\nTodos los kyrianos aprenden a luchar en sus cámaras sagradas. No solo para proteger Bastión, sino para garantizar su seguridad al recuperar almas del otro lado del velo.\n\nViaja hasta allí de inmediato. Busca a Thanikos, la leal mano del dechado del coraje.', '', '', '', '', '', 'Busca a Thanikos en la Vigilia de Xandria.', 0);
INSERT INTO `quest_template_locale` VALUES (60005, 'esMX', 'Peligro inminente', 'Busca a Thanikos en Vigilia de Xandria.', '¡El Templo del Valor no debe caer!\n\nTodos los kyrian aprenden a luchar en sus salones sagrados. No es solo para proteger a Bastión, sino para garantizar su propia seguridad cuando traen almas del otro lado del velo.\n\nVe ahí de inmediato. Busca a Thanikos, la mano leal de la Dechada del valor.', '', '', '', '', '', 'Busca a Thanikos en Vigilia de Xandria.', 0);
INSERT INTO `quest_template_locale` VALUES (60005, 'frFR', 'Menace imminente', 'Allez voir Thanikos à la Veille de Xandria.', 'Le temple du Courage ne doit pas tomber !\n\nTous les Kyrians apprennent à se battre dans ses salles sacrées. Non seulement pour défendre le Bastion, mais aussi pour assurer leur propre sécurité lorsqu’ils partent récupérer des âmes de l’autre côté du voile.\n\nRendez-vous immédiatement sur place et allez trouver Thanikos, le fidèle bras droit du parangon de courage.', '', '', '', '', '', 'Allez voir Thanikos à la Veille de Xandria.', 0);
INSERT INTO `quest_template_locale` VALUES (60005, 'itIT', 'Pericolo incombente', 'Cerca Thanikos alla Veglia di Xandria.', 'Il Tempio del Coraggio non deve cadere!\n\nTutti i Kyrian hanno imparato a combattere nelle sue sacre sale, non solo per proteggere il Bastione, ma per garantire la loro stessa sicurezza nel recuperare le anime da oltre il velo.\n\nVa\' immediatamente lì. Cerca Thanikos, la Mano del Coraggio.', '', '', '', '', '', 'Cerca Thanikos alla Veglia di Xandria.', 0);
INSERT INTO `quest_template_locale` VALUES (60005, 'koKR', '임박한 위험', '잔드리아의 망루에서 타니코스를 찾아야 합니다.', '용기의 사원이 함락되어선 안 된다!\n\n모든 키리안이 그 신성한 전당에서 싸우는 법을 배우지, 단순히 승천의 보루를 지키는 걸 넘어 장막 저편에서 영혼을 데려올 때 안정을 보장하기 위한 하나의 수단이다. \n\n당장 사원으로 가라. 용기의 용장의 충직한 손 타니코스를 찾아야 한다.', '', '', '', '', '', '잔드리아의 망루에서 타니코스를 찾아야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (60005, 'ptBR', 'Perigo iminente', 'Procure Thânikos na Vigia de Xândria.', 'O Templo da Coragem não pode cair!\n\nTodo kyriano aprende a lutar em seus salões sagrados. Não apenas para proteger o Bastião, mas para garantir a própria segurança ao resgatar as almas para além do véu. \n\nSiga para lá imediatamente. Procure Thânikos, braço direito da Paragona da Coragem.', '', '', '', '', '', 'Procure Thânikos na Vigia de Xândria.', 0);
INSERT INTO `quest_template_locale` VALUES (60005, 'ruRU', 'Неотвратимая опасность', 'Найдите Таникоса в Дозоре Ксандрии.', 'Храм Отваги не должен пасть!\n\nВ его священных чертогах учатся сражаться все кирии. Не только для защиты Бастиона, но и чтобы меньше рисковать, перенося души через завесу. \n\nОтправляйся туда как можно скорее. Найди Таникоса, верного помощника идеала Отваги.', '', '', '', '', '', 'Найдите Таникоса в Дозоре Ксандрии.', 0);
INSERT INTO `quest_template_locale` VALUES (60005, 'zhCN', '迫在眉睫', '在山德里娅的戒卫寻找萨尼科斯。', '勇气神庙绝不能沦陷。\n\n所有格里恩都是在那里的神圣殿堂中学会如何战斗的。这不仅是为了保护晋升堡垒，也是为了在护送穿过帷幕的灵魂时能确保自己的安全。\n\n再去一次那里吧。找到忠诚的勇气圣杰之手，萨尼科斯。', '', '', '', '', '', '在山德里娅的戒卫寻找萨尼科斯。', 0);
INSERT INTO `quest_template_locale` VALUES (60005, 'zhTW', '岌岌可危', '到珊卓亞的守望找到杉尼寇斯。', '勇氣神殿絕不能陷落。\n\n所有琪瑞安都在這神聖的殿堂學習戰鬥。不只為了保護昇靈堡，也為了在穿過帷幕引渡靈魂時保護自己。\n\n立刻前往那裡，找到杉尼寇斯，他是勇氣楷模的效忠者。', '', '', '', '', '', '到珊卓亞的守望找到杉尼寇斯。', 0);
INSERT INTO `quest_template_locale` VALUES (60006, 'deDE', 'Jetzt oder nie', 'Erobert Xandrias Wacht zurück.', 'In einem fairen Kampf könnten unsere Krieger gegen zahllose Schwärme dieser Maldraxxi bestehen.\n\nDies war kein fairer Kampf, Fremdling.\n\nSie kamen ohne Vorwarnung. Ohne Grund. Wir wurden unvorbereitet getroffen.\n\nSie dürfen nicht noch weiter vordringen. Das muss hier aufhören.', '', '', '', '', '', 'Sprecht mit Thanikos in Xandrias Wacht.', 0);
INSERT INTO `quest_template_locale` VALUES (60006, 'esES', 'Ahora o nunca', 'Recupera la Vigilia de Xandria.', 'En una lucha justa, nuestros guerreros resistirían incontables oleadas de estos maldraxxi.\n\nPero esta no ha sido una lucha justa, $gforastero:forastera;.\n\nVinieron sin avisar. Sin motivo. Por sorpresa.\n\nNo podemos permitir que sigan avanzando. Debemos acabar con esto.', '', '', '', '', '', 'Habla con Thanikos, que está en la Vigilia de Xandria.', 0);
INSERT INTO `quest_template_locale` VALUES (60006, 'esMX', 'Ahora o nunca', 'Recupera la Vigilia de Xandria.', 'En una pelea justa, nuestros guerreros pueden contra una infinidad de enjambres de estos maldraxxi.\n\nPero aquí no hubo nada justo, $gforastero:forastera;.\n\nVinieron sin advertencia. Sin motivo. Nos tomaron por sorpresa.\n\nNo podemos permitirles que sigan invadiéndonos. Esto se acaba aquí.', '', '', '', '', '', 'Habla con Thanikos en Vigilia de Xandria.', 0);
INSERT INTO `quest_template_locale` VALUES (60006, 'frFR', 'Maintenant ou jamais', 'Reprenez la Veille de Xandria.', 'Dans un combat loyal, nos guerriers n’auraient eu aucun mal à repousser des hordes entières de ces Maldraxxi.\n\nMais ce n’était pas un combat loyal, $gétranger:étrangère;.\n\nIls ont attaqué sans prévenir. Sans raison. Nous avons été pris au dépourvu.\n\nPas question de les laisser progresser davantage. Nous allons les arrêter ici.', '', '', '', '', '', 'Parlez à Thanikos à la Veille de Xandria.', 0);
INSERT INTO `quest_template_locale` VALUES (60006, 'itIT', 'Ora o mai più', 'Riconquista la Veglia di Xandria.', 'In uno scontro leale i nostri guerrieri riuscirebbero a resistere a infinite ondate di questi Maldraxxi.\n\nMa questo non è stato uno scontro leale, $gstraniero:straniera;.\n\nSono arrivati senza preavviso e senza motivo. Ci hanno colti impreparati.\n\nNon dobbiamo permettere loro di avanzare oltre, questo assalto finisce qui.', '', '', '', '', '', 'Parla con Thanikos alla Veglia di Xandria.', 0);
INSERT INTO `quest_template_locale` VALUES (60006, 'koKR', '일생일대의 기회', '잔드리아의 망루를 탈환해야 합니다.', '정정당당한 승부라면 말드락시가 얼마나 몰려오든 우리 전사들이 항전할 수 있소.\n\n그렇지 않아서 문제지.\n\n놈들은 느닷없이 들이닥쳤다오. 아무 명분 없이. 그 탓에 우린 무방비한 상태로 당했소.\n\n이 이상 포위망을 좁히게 허용해선 아니 되오. 여기서 막읍시다.', '', '', '', '', '', '잔드리아의 망루에서 타니코스와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (60006, 'ptBR', 'Agora ou nunca', 'Retome a Vigia de Xândria.', 'Numa luta justa nossos guerreiros conseguiriam resistir a incontáveis enxames desses maldraxxis.\n\nEssa luta não foi justa, forasteir$go:a;.\n\nEles chegaram sem aviso. Sem motivo. Nos pegaram despreparados.\n\nNão podemos permitir que avancem mais. Isso acaba aqui.', '', '', '', '', '', 'Fale com Thânikos na Vigia de Xândria.', 0);
INSERT INTO `quest_template_locale` VALUES (60006, 'ruRU', 'Сейчас или никогда', 'Отбейте Дозор Ксандрии у врага.', 'В честном бою наши воины выстояли бы против бесчисленных полчищ из Малдраксуса.\n\nНо этот бой не был честным, $gчужестранец:чужестранка;.\n\nОни напали без предупреждения. Без причины. Нас застали врасплох.\n\nНельзя позволить им продвинуться дальше. Их нужно остановить здесь.', '', '', '', '', '', 'Поговорите с Таникосом в Дозоре Ксандрии.', 0);
INSERT INTO `quest_template_locale` VALUES (60006, 'zhCN', '机不可失', '夺回山德里娅的戒卫。', '如果是公平对决，我们的战士可以对抗无数玛卓克萨斯的乌合之众。\n\n但是这里没有公平的对决，外来者。\n\n他们毫无预兆地来袭，毫无缘由。我们措手不及。\n\n我们决不能让他们继续入侵了，必须在这里阻止他们。', '', '', '', '', '', '前往山德里娅的戒卫与萨尼科斯交谈。', 0);
INSERT INTO `quest_template_locale` VALUES (60006, 'zhTW', '刻不容緩', '奪回珊卓亞的守望。', '在公平對決的條件下，我們的戰士能夠抵擋這源源不絕的瑪卓薩斯軍隊。\n\n但這不是公平對決，外來者。\n\n他們來勢洶洶，卻毫無預警，毫無原由。我們只能倉促應戰。\n\n絕不能允許他們繼續前進一步，一切到此為止。', '', '', '', '', '', '到珊卓亞的守望與杉尼寇斯交談。', 0);
INSERT INTO `quest_template_locale` VALUES (60007, 'deDE', 'Schrottverwertung', 'Bergt den Animakonflux, die Ewigkeitspanzerung und den archonischen Resonator.', 'Ich kann die Kernsysteme dieses Kolosses wiederherstellen, aber einige Dinge sind irreparabel. Zum Glück gab es hier genug Zenturionen. Ich glaube, dass wir improvisieren können.\n\nIch brauche Ersatzteile!\n\nIch benötige einen Animakonflux, um seine Systeme aufzuladen, eine Ewigkeitspanzerung, um den Kern zu schützen, und einen archonischen Resonator, damit es wieder rumst!\n\nIch kümmere mich um den Rest. Schnell, schnell!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60007, 'esES', 'Qué trasto eres', 'Consigue una confluencia de ánima, un blindaje de la eternidad y un resonador arcóntico.', 'Puedo reconstruir los sistemas principales del coloso, pero algunas cosas no se pueden reparar. Por suerte, había suficientes centuriones aquí, así que podemos improvisar algo.\n\n¡Hacen falta repuestos!\n\nNecesito una confluencia de ánima para cargar sus sistemas, un poco de blindaje de la eternidad para proteger su núcleo ¡y un resonador arcóntico para que haga BUM!\n\nYo me encargaré del resto. ¡Venga, rápido!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60007, 'esMX', 'Entre chatarra', 'Recupera la confluencia de ánima, el blindaje de la eternidad y el resonador arcóntico.', 'Puedo reconstruir los sistemas centrales de este coloso, pero hay algunas cosas que son irreparables. Por suerte, había suficientes centuriones aquí, así que creo que podemos improvisar.\n\n¡Necesitamos repuestos!\n\nPreciso una confluencia de ánima para cargar sus sistemas, un poco de blindaje de la eternidad para proteger su núcleo ¡y un resonador arcóntico para que haga bum!\n\nY yo me ocuparé del resto. ¡Rápido, ya!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60007, 'frFR', 'Pas de répit pour les braves', 'Récupérez la Confluence d’anima, la Plaque éternelle et le Résonateur archonique.', 'Je peux réparer les systèmes internes de ce colosse, mais certains éléments doivent être remplacés. Heureusement, cet endroit abritait suffisamment de centurions pour nous permettre d’improviser.\n\nAu travail !\n\nIl me faut une confluence d’anima pour charger ses systèmes, une plaque d’éternité pour protéger son noyau et un résonateur archonique pour qu’il puisse faire boum !\n\nJe me chargerai du reste. Dépêchez-vous !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60007, 'itIT', 'Gli scarti di Scarty', 'Recupera il Canalizzatore d\'Animum, i Rivestimenti Eterni e il Risonatore Arcontico.', 'Posso ricostruire i sistemi principali di questo Colosso, ma non posso riparare tutto. Fortunatamente c\'erano abbastanza Centurioni e credo di poter improvvisare qualcosa.\n\nI ricambi sono in arrivo!\n\nMi serve un Canalizzatore d\'Animum per caricare i sistemi, dei Rivestimenti Eterni per proteggere il nucleo e un Risonatore Arcontico per fargli fare il botto!\n\nPenserò io al resto. Su, presto!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60007, 'koKR', '한결같은 재활용 생활', '령의 합일체, 영원의 장갑판, 집정관 공명체를 회수해야 합니다.', '이 거신의 핵을 복원하라면 복원할 수 있겠지만, 도저히 수리할 가망이 안 보이는 부품이 몇 개 있소. 그래도 주변에 백인대장이 여럿 있으니 급조 못 할 건 아니지.\n\n필요한 대체품을 알려주겠소!\n\n장치를 충전할 령의 합일체, 핵을 보호할 영원의 장갑판, 폭발적인 위력을 낼 집정관 공명체가 필요하오!\n\n나머지는 알아서 해결할 테니, 얼른 서둘러 주시오!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60007, 'ptBR', 'Quanto mais sucata, melhor', 'Recupere a Confluência Anímica, a Blindagem da Eternidade e o Ressonador Arcôntico.', 'Consigo reconstruir os sistemas principais desse colosso, mas algumas coisas deram perda total. Por sorte havia centuriões suficientes aqui para eu achar que podemos improvisar.\n\nPeças de reposição estão na ordem do dia!\n\nPreciso de uma Confluência Anímica para carregar o sistema, de alguma Blindagem da Eternidade para proteger o núcleo e de um Ressonador Arcôntico pra fazer o cabum!\n\nO resto é comigo. Rápido!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60007, 'ruRU', 'С миру по детали', 'Добудьте накопитель анимы, вечные пластины и резонатор Архонта.', 'Я могу отремонтировать основные системы этого колосса, но кое-что не подлежит восстановлению. К счастью, здесь было достаточно центурионов, и, думаю, мы сможем найти что-нибудь подходящее.\n\nНужно заменить некоторые детали!\n\nМне нужен набат анимы, чтобы зарядить его системы, немного вечных пластин, чтобы защитить его ядро, и резонатор Архонта, чтобы наш колосс мог задать жару!\n\nОб остальном я позабочусь. Быстрее!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60007, 'zhCN', '保持斗志', '获得心能流汇、永恒装甲和执政共鸣水晶。', '我可以重新建立这个巨像的核心系统，但有些地方已经无法修复了。好在这里有许多百心长，我相信我们可以即兴发挥一下。\n\n替换品已准备就绪！\n\n我需要一个心能流汇来为它的系统充电，需要一些永恒装甲来保护它的核心，还要一个执政共鸣水晶来让它动起来！\n\n然后剩下的就可以交给我了。快行动吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60007, 'zhTW', '逞凶鬥狠', '取回靈魄匯流裝置、永恆裝甲及御靈共鳴器。', '我可以重建這個巨像的核心系統，不過有些地方根本無法修復。幸好，這裡的百衛機兵數量夠多，我相信我們可以變通一下。\n\n該來換上替換零件了！\n\n我需要靈魄匯流裝置為系統充能，還需要一些永恆裝甲保護它的核心，最後需要御靈共鳴器讓它動起來！\n\n剩下的事交給我，快點去找吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60008, 'deDE', 'Zerreißen und zerfetzen', 'Tötet Truppen vom Haus der Konstrukte.', 'Wir werden den maldraxxischen Aufrührern den Preis für ihren Verrat zeigen.\n\nDie nördlichen Gebiete wurden vom Haus der Konstrukte übernommen, der Heimat der unverwüstlichsten Krieger aus Maldraxxus.\n\nDie Aufrührer haben sich heute keine Gnade verdient und wir werden ihnen keine gewähren. Wir werden sie Seite an Seite niedermetzeln, bis dieser heilige Boden gesäubert ist.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60008, 'esES', 'Destripar y desgarrar', 'Mata a las fuerzas de la Casa de los Ensamblajes.', 'Les enseñaremos a estos insurgentes maldraxxi el precio de su traición.\n\nLa Casa de los Ensamblajes ha tomado la zona norte, cuna de los guerreros maldraxxi más resistentes.\n\nNo se han ganado nuestra misericordia y no se la concederemos. Nos abriremos paso juntos a través de ellos hasta que este terreno sagrado quede purificado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60008, 'esMX', 'Rompe y destroza', 'Mata fuerzas de la Casa de Ensamblajes.', 'Les enseñaremos las consecuencias de su traición a estos maldraxxi insurgentes.\n\nLos terrenos del norte están tomados por la Casa de Ensamblajes, el hogar de los guerreros más resistentes de Maldraxxus.\n\nNo merecen piedad, y tampoco se la regalaremos. Haremos pedazos a cuantos haga falta hasta purificar esta tierra sagrada.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60008, 'frFR', 'Lacérer et déchirer', 'Tuez les troupes de la Maison des Assemblages.', 'Nous allons faire payer à ces insurgés maldraxxi le prix de leur trahison.\n\nLe nord de l’enceinte est entre les mains de la maison des Assemblages qui fournit à Maldraxxus ses guerriers les plus robustes.\n\nIls ne méritent aucune pitié en ce jour, et nous ne leur en témoignerons aucune. Vous et moi allons purifier ce lieu saint en les taillant en pièces.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60008, 'itIT', 'Lacera e strappa', 'Uccidi le forze della Casata dei Costrutti.', 'Mostreremo a questi Maldraxxi il prezzo del loro tradimento.\n\nI territori settentrionali sono stati presi dalla Casata dei Costrutti, responsabile dei guerrieri più resilienti di Maldraxxus.\n\nQuest\'oggi non si sono guadagnati alcuna pietà, e noi non gliene mostreremo. Ci faremo strada tra di loro fianco a fianco finché non purificheremo questo terreno sacro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60008, 'koKR', '찢고 도려내리', '피조물의 의회 병력을 처치해야 합니다.', '우린 말드락시 반란 분자 놈들에게 통렬한 배반의 대가를 받아낼 거요.\n\n피조물의 의회가 북쪽 지대를 점령했소. 말드락서스에서 끈질기기로는 제일가는 전사들이지.\n\n놈들이 저지른 만행이 있으니 절대 자비를 베풀어 주지 않을 것이오. 성지를 정화하는 그 순간까지 모조리 도륙 내 버릴 작정이외다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60008, 'ptBR', 'Rasgar e cortar', 'Mate as forças da Casa dos Constructos.', 'Vamos mostrar a esses maldraxxis insurrectos qual é o preço da traição.\n\nO setor norte foi tomado pela Casa dos Constructos, os guerreiros mais resistentes de Maldraxxus.\n\nHoje eles não fizeram por merecer qualquer piedade, e não teremos nenhuma. Abriremos caminho pelo meio deles até que o solo sagrado seja purificado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60008, 'ruRU', 'Рвать и терзать', 'Разбейте силы дома Кадавров.', 'Покажем этим мятежникам из Малдраксуса, какая судьба ждет всех предателей.\n\nНа севере закрепился дом Кадавров, к которому принадлежат самые стойкие воины Малдраксуса.\n\nСегодня они не заслужили пощады – и мы не будем их щадить. Мы будем уничтожать их, ты и я, пока не очистим эти священные земли.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60008, 'zhCN', '狂怒撕扯', '消灭造物密院的部队。', '我们会让这些玛卓克萨斯叛徒为自己的背叛付出代价。\n\n北部土地已经被造物密院占领，那里有玛卓克萨斯最坚韧的战士。\n\n他们今天不会获得怜悯，我们也不会给他们怜悯，我们会把他们逐个消灭，直到这片神圣的土地得以净化。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60008, 'zhTW', '大解八塊', '殺死傀儡家族的部隊。', '我們會讓這些瑪卓薩斯叛軍嚐嚐背叛的代價。\n\n北方地帶已經被傀儡家族佔領，瑪卓薩斯最驍勇善戰的戰士都出自這個家族。\n\n他們今天根本毫不留情，那麼我們也不會憐憫他們。我們會並肩作戰，殺進殺出，直到這片神聖土地得到淨化為止。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60013, 'deDE', 'Überlasst das Mnemis', 'Benutzt die Transportplattform in der Kammer der Archon.', 'Ich habe die Dringlichkeit in all Euren Stimmen vernommen, dass Ihr uns verlassen möchtet.\n\nIch werde Eure Abreise beschleunigen. Es gibt eine Transportplattform in der Vorkammer. Ich werde dafür sorgen, dass die Kammer nach Eurer Abreise wieder versiegelt wird.\n\nBitte folgt mir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60013, 'esES', 'Déjaselo a Nemis', 'Usa la plataforma de transporte en la Cámara de la Arconte.', 'Por la urgencia de vuestras voces colectivas he deducido que queréis marcharos.\n\nAgilizaré vuestra salida. Hay una plataforma de transporte en la antecámara. Me aseguraré de que la cámara se quede sellada cuando salgáis.\n\nAcompañadme.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60013, 'esMX', 'Confía en Mnemis', 'Usa la plataforma de transporte de la Bóveda de la Arconte.', 'Deduzco, por la urgencia de sus voces, que desean marcharse.\n\nAceleraré su partida. Hay una plataforma de transporte en la antecámara. Me aseguraré de que la cámara quede sellada nuevamente cuando salgan.\n\nAcompáñenme.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60013, 'frFR', 'Laissez faire Mnémès', 'Utilisez la Plateforme de transport dans le Caveau de l’Archonte.', 'L’urgence que je détecte dans vos voix m’informe que vous souhaitez partir.\n\nJe vais hâter votre départ. L’antichambre abrite une plateforme de transport. Je scellerai à nouveau ces lieux lorsque vous serez partis.\n\nVeuillez me suivre, je vous prie.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60013, 'itIT', 'Lascia che ci pensi Mnemi', 'Usa la Piattaforma di Trasporto nell\'Archivio dell\'Arconte.', 'Ho dedotto dall\'urgenza che sento nelle vostre voci che desiderate andarvene.\n\nVelocizzerò la vostra uscita. C\'è una piattaforma di trasporto nell\'anticamera, mi assicurerò che tale camera venga chiusa di nuovo dopo la vostra uscita.\n\nSeguitemi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60013, 'koKR', '네미스에게 믿고 맡겨라 이 말씀', '집정관의 보관소에서 순간이동 장치를 사용해야 합니다.', '떠나야 한다고 말씀하실 때의 목소리를 단서로 추론해보자면 촌각을 다투는 긴박한 상황에 부닥치셨나 보군요.\n\n빠른 퇴장을 도와드리겠습니다. 대기실에 순간이동 장치가 구비되어 있습니다. 방은 퇴장하시는 대로 책임지고 다시 봉인해 두겠습니다.\n\n그럼 따라오십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60013, 'ptBR', 'Deixa com Mnêmis', 'Use a Plataforma de Transporte na Abóbada da Arconte.', 'Eu deduzo pela urgência em suas vozes que vocês desejam partir.\n\nEu agilizarei sua saída. Há uma plataforma de transporte na antecâmara. Eu me certificarei de selar a câmara depois que vocês se forem.\n\nQueiram me acompanhar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60013, 'ruRU', 'Об этом позаботится \"Мнемида\"', 'Воспользуйтесь телепортационной платформой в хранилище Архонта.', 'Я слышу в ваших голосах волнение и делаю вывод, что вы хотите уйти.\n\nЯ обеспечу вам выход. В вестибюле находится телепортационная платформа. Когда вы уйдете, я вновь запечатаю Хранилище.\n\nПожалуйста, следуйте за мной.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60013, 'zhCN', '交给念魔姬', '使用执政官宝地的传送器。', '我从你们话语的急迫中感觉到你们想要离开。\n\n我会帮助你们离开，前厅有一个传送器，你们离开之后我会把传送器再次关闭。\n\n请跟我来。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (60013, 'zhTW', '助憶事項', '使用御靈者寶庫的傳送板。', '根據你著急的語氣，可以推斷出你希望離開。\n\n我會讓你盡快離開這裡。前廳有個傳送板，我會確保前廳在你離開之後，再次封閉起來。\n\n請跟我來。', '', '', '', '', '', '', 0);
